/**
 * 
 * @sessionid:
 * 
 * @return:
 */
SaErrorT populate_saHpiDomainAlarmTable(SaHpiSessionIdT sessionid)
{
	SaErrorT rv = SA_OK;
        SaErrorT rc = SA_OK;

	oid domain_alarm_oid[DOMAIN_ALARM_INDEX_NR];
	netsnmp_index domain_alarm_idx;
	saHpiDomainAlarmTable_context *domain_alarm_ctx;
	
	/* Used for saHpiDomainAlarmGetNext call */
        SaHpiSeverityT Severity;
	SaHpiAlarmT Alarm;
	
	/* Used for decoding */
	oh_big_textbuffer bigbuf;
	SaHpiTextBufferT buf;
	
	/* Used for mapping */
        DE_XREF *de_entry;
        doma_keys keys;
	
        DEBUGMSGTL ((AGENT, "populate_saHpiDomainAlarmTable, called\n"));		
		
       	/* Get the first DAT Entry for this Session */	
	Alarm.AlarmId = SAHPI_FIRST_ENTRY;
	/* Get all the severities */
	Severity = SAHPI_ALL_SEVERITIES;
	
	rv = saHpiAlarmGetNext( sessionid,
		                Severity,
                                SAHPI_FALSE, //Get ALL Alarms
				&Alarm);
	
	if ((rv != SA_OK) && (rv != SA_ERR_HPI_NOT_PRESENT)) {
		DEBUGMSGTL ((AGENT, 
		"populate_saHpiDomainAlarmTable: saHpiDomainAlarmGetNext Failed: rv = %d\n",
		rv));
		return AGENT_ERR_INTERNAL_ERROR;
	}
	else if (rv == SA_ERR_HPI_NOT_PRESENT) {
	        return SA_OK; //first call returned nothing
	}
	
	while ((rv != SA_ERR_HPI_NOT_PRESENT) && (rv == SA_OK)) {

                /* Store sa value                                       */
                /* this is the same as the AlarmId obtained from HPI    */
                /* BUT in the case of a user defined Alarm they  */
                /* will be different                                    */
                keys.key_array[0] = Alarm.AlarmCond.DomainId;
                keys.key_array[1] = Alarm.AlarmId;

                /* domain_alarm_entry_get() generates unique keys based on */
                /* domainid and alarmid values.                            */
                de_entry = domain_alarm_entry_get(&keys, &doma_de_table); 

                if (de_entry == NULL) {
                        DEBUGMSGTL ((AGENT, 
                                     "ERROR: populate_saHpiDomainAlarmTable() "
                                     "domain_alarm_entry_get returned NULL\n"));
                        return AGENT_ERR_INTERNAL_ERROR;
                }
                /* here is where the mapped id is the same as the key value     */
                /* remember when the user creates an announcement the user      */
                /* specifies the entryId and will be different from the         */
                /* entryId returned from the HPI API that generates the         */
                /* new announcement                                             */    
                de_entry->hpi_alarm_id = Alarm.AlarmId;

	        
		/* assign the number of indices */
	        domain_alarm_idx.len = DOMAIN_ALARM_INDEX_NR;
		/** Index saHpiDomainId is external */
	        domain_alarm_oid[0] = Alarm.AlarmCond.DomainId; //Is this Right??
	        /** Index saHpiDomainAlarmId is internal */
                domain_alarm_oid[1] = Alarm.AlarmId;
	        /** Index saHpiDomainAlarmSeverity is internal */
	        domain_alarm_oid[2] = Alarm.Severity;
		
	        domain_alarm_idx.oids = (oid *) & domain_alarm_oid;
	   
	        /* See if Row exists. */
	        domain_alarm_ctx = NULL;
	        domain_alarm_ctx = CONTAINER_FIND(cb.container, &domain_alarm_idx);

	        if (!domain_alarm_ctx) { 
		        // New entry. Add it
		        domain_alarm_ctx = 
			        saHpiDomainAlarmTable_create_row(&domain_alarm_idx);
	        }
	        if (!domain_alarm_ctx) {
		        snmp_log (LOG_ERR, "Not enough memory for a Domain Alarm row!");
		        return AGENT_ERR_INTERNAL_ERROR;
	        }

                /** SaHpiEntryId = ASN_UNSIGNED */
                domain_alarm_ctx->saHpiDomainAlarmId = Alarm.AlarmId;
	
                /** SaHpiTime = ASN_COUNTER64 */
	        domain_alarm_ctx->saHpiDomainAlarmTimestamp = Alarm.Timestamp;
	
                /** TruthValue = ASN_INTEGER */
	        domain_alarm_ctx->saHpiDomainAlarmAcknowledged = 
                        (Alarm.Acknowledged == SAHPI_TRUE)
                        ? MIB_TRUE : MIB_FALSE; 

                /** TruthValue = ASN_INTEGER */
	        domain_alarm_ctx->saHpiDomainAlarmAckBySeverity = 0;
	
	        /** INTEGER = ASN_INTEGER */
        	domain_alarm_ctx->saHpiDomainAlarmCondStatusCondType = Alarm.AlarmCond.Type + 1;
		
		/** SaHpiEntityPath = ASN_OCTET_STR */
                rc = oh_decode_entitypath(&Alarm.AlarmCond.Entity, &bigbuf);
		
		if (rc != SA_OK) {
		        DEBUGMSGTL ((AGENT, 
		          "populate_saHpiDomainAlarmTable: oh_decode_entitypath Failed: rc = %d\n",
		           rc));
		}
		else {
		       memcpy(domain_alarm_ctx->saHpiDomainAlarmCondEntityPath,
		              bigbuf.Data, bigbuf.DataLength);
	               domain_alarm_ctx->saHpiDomainAlarmCondEntityPath_len = bigbuf.DataLength;
		}
	
		
		if (Alarm.AlarmCond.Type == SAHPI_STATUS_COND_TYPE_SENSOR) {
		
		        /** UNSIGNED32 = ASN_UNSIGNED */
			domain_alarm_ctx->saHpiDomainAlarmCondSensorNum = Alarm.AlarmCond.SensorNum;
			
		        /** SaHpiEventState = ASN_OCTET_STR */
		        rc = oh_decode_eventstate(Alarm.AlarmCond.EventState, SAHPI_EC_SENSOR_SPECIFIC, &buf);
			
			if (rc != SA_OK) {
		                DEBUGMSGTL ((AGENT, 
		                "populate_saHpiDomainAlarmTable: oh_decode_eventstate Failed: rc = %d\n",
		                 rc));
			}	 
			else {	 		        
		                memcpy(domain_alarm_ctx->saHpiDomainAlarmCondEventState, 
				                                buf.Data, buf.DataLength); 
								
				domain_alarm_ctx->saHpiDomainAlarmCondEventState_len = buf.DataLength;
			}					
		}

	        /** OCTETSTR = ASN_OCTET_STR */
		memcpy(domain_alarm_ctx->saHpiDomainAlarmCondNameValue, 
		                Alarm.AlarmCond.Name.Value, Alarm.AlarmCond.Name.Length);
		
	        domain_alarm_ctx->saHpiDomainAlarmCondNameValue_len = Alarm.AlarmCond.Name.Length;
		
		/** SaHpiManufacturerId = ASN_UNSIGNED */
		domain_alarm_ctx->saHpiDomainAlarmCondMid = Alarm.AlarmCond.Mid;
		
	        /** SaHpiTextType = ASN_INTEGER */
                domain_alarm_ctx->saHpiDomainAlarmCondTextType = Alarm.AlarmCond.Data.DataType + 1;
	        
		/** SaHpiTextLanguage = ASN_INTEGER */
		domain_alarm_ctx->saHpiDomainAlarmCondTextLanguage = Alarm.AlarmCond.Data.Language + 1;
        
	        /** SaHpiText = ASN_OCTET_STR */
                domain_alarm_ctx->saHpiDomainAlarmCondText_len = Alarm.AlarmCond.Data.DataLength;
		
		memcpy(domain_alarm_ctx->saHpiDomainAlarmCondText, 
		                        Alarm.AlarmCond.Data.Data, Alarm.AlarmCond.Data.DataLength);
					
		domain_alarm_ctx->saHpiDomainAlarmRowStatus = SAHPIDOMAINALARMROWSTATUS_ACTIVE;
		
	        CONTAINER_INSERT (cb.container, domain_alarm_ctx);
	
	        //Get the next Alarm
        	rv = saHpiAlarmGetNext( sessionid,
		                        Severity,
                                        SAHPI_TRUE, //Get Unacknowledged Alarms
				        &Alarm);
		
        }	
	
	domain_alarm_entry_count = CONTAINER_SIZE (cb.container);

        return SA_OK;   								    
}


/**
 * 
 * @param row_ctx
 * 
 * @return 
 */
int domain_alarm_delete (saHpiDomainAlarmTable_context *row_ctx)
{
        DEBUGMSGTL ((AGENT, "domain_alarm_delete, called\n"));

        SaErrorT            rc = SA_OK;
        SaHpiSessionIdT     session_id;
        SaHpiAlarmIdT       alarmId;

        DE_XREF *de_entry;
        doma_keys keys;

        if (!row_ctx)
                return AGENT_ERR_NULL_DATA;

        session_id = get_session_id(row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX]);

        keys.key_array[0] = row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX];
        keys.key_array[1] = row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX];

        /* domain_alarm_entry_get() generates unique keys based on */
        /* domainid and entryid.                                   */
        de_entry = domain_alarm_entry_get(&keys, &doma_de_table); 

        if (de_entry == NULL) {
                DEBUGMSGTL ((AGENT, 
                             "ERROR: domain_alarm_delete() "
                             "domain_alarm_entry_get returned NULL\n"));
                return AGENT_ERR_INTERNAL_ERROR;
        }

        /* see if this is an attempt to delete based on severtiy */
        if (row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX] == 0) {
                /* we are deleting based on Severity */
                alarmId = SAHPI_ENTRY_UNSPECIFIED;
        } else {
                /* we have retrieved the HPI Alarm ID  */
                /* now we can call the delete function */
                alarmId = de_entry->hpi_alarm_id;
        }

        rc = saHpiAlarmDelete(session_id, 
                              alarmId,
                              row_ctx->saHpiDomainAlarmSeverity - 1);
        if (rc != SA_OK) {
                snmp_log (LOG_ERR,
                "domain_alarm_delete: Call to "
                "saHpiAlarmDelete() failed rc: %s.\n",
                oh_lookup_error(rc));
                DEBUGMSGTL ((AGENT,
                "domain_alarm_delete: Call to "
                "saHpiAlarmDelete() failed rc: %s.\n",
                oh_lookup_error(rc)));
                return get_snmp_error(rc);
        } 

        DEBUGMSGTL ((AGENT, "domain_alarm_delete: Call to "
                   "saHpiAlarmDelete() succeeded rc: %s.\n",
                   oh_lookup_error(rc)));

        /* if we deleted based on severity then we need to remove      */
        /* all rows for the given SessionId <==> DomainId, ResourceId, */
        /* AnnunciatorNum and Severity                                 */
        if (alarmId == SAHPI_ENTRY_UNSPECIFIED) {

                netsnmp_index *row_idx;
                saHpiDomainAlarmTable_context *da_ctx;

                row_idx = CONTAINER_FIRST(cb.container);
                if (row_idx) //At least one entry was found.
                {
                        do {
                                da_ctx = CONTAINER_FIND(cb.container, row_idx);

                                if ((da_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX] ==
                                     row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX]) &&
                                    
				    ((da_ctx->saHpiDomainAlarmSeverity == 
                                     row_ctx->saHpiDomainAlarmSeverity) || 
				    (row_ctx->saHpiDomainAlarmSeverity == 
                                     SAHPI_ALL_SEVERITIES)) &&
                                    
				    (da_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX] !=
                                     row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX])) {
                                        /* all conditions met remove row */
                                        CONTAINER_REMOVE (cb.container, da_ctx);
                                        saHpiDomainAlarmTable_delete_row (da_ctx);
                                        domain_alarm_entry_count = CONTAINER_SIZE (cb.container);
                                        DEBUGMSGTL ((AGENT, "domain_alarm_delete: found row for "
                                                     "deletion based on severity\n"));
                                }
                                row_idx = CONTAINER_NEXT(cb.container, row_idx);
                        } while (row_idx);
                }
        } /* end check for all rows deleted based on Severity */

        return SNMP_ERR_NOERROR; 
}

/**
 * 
 * @param row_ctx
 * 
 * @return 
 */
int domain_alarm_add (saHpiDomainAlarmTable_context *row_ctx)
{
        DEBUGMSGTL ((AGENT, "domain_alarm_add, called\n"));

        SaErrorT            rc = SA_OK;
        SaHpiSessionIdT     session_id;
        SaHpiAlarmT         alarm;

        DE_XREF *de_entry;
        doma_keys keys;
        
        if ((row_ctx->sahpi_domain_alarm_severity_set         == MIB_TRUE) &&
            (row_ctx->sahpi_domain_alarm_status_cond_type_set == MIB_TRUE) &&
            (row_ctx->sahpi_domain_alarm_entitypath_set       == MIB_TRUE) &&                 
            (row_ctx->sahpi_domain_alarm_name_set             == MIB_TRUE) &&                           
            (row_ctx->sahpi_domain_alarm_text_type_set        == MIB_TRUE) &&   
            (row_ctx->sahpi_domain_alarm_text_language_set    == MIB_TRUE) &&
            (row_ctx->sahpi_domain_alarm_text                 == MIB_TRUE) &&
            (row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX] !=
             SAHPI_ENTRY_UNSPECIFIED)) {

                /* EntryId */
                alarm.AlarmId = row_ctx->saHpiDomainAlarmId;

                /* Timestamp */
                alarm.Timestamp = 
                        row_ctx->saHpiDomainAlarmTimestamp;

		/* Severity */
		alarm.Severity =
			row_ctx->saHpiDomainAlarmSeverity;
			
                /* Acknowledged */
                alarm.Acknowledged = 
                        (row_ctx->saHpiDomainAlarmAcknowledged == MIB_TRUE) ? 
                        SAHPI_TRUE : SAHPI_FALSE;			
                
		/*StatusCond */		
                alarm.AlarmCond.Type = 
                        row_ctx->saHpiDomainAlarmCondStatusCondType - 1;	
			
                rc = oh_encode_entitypath(row_ctx->saHpiDomainAlarmCondEntityPath, 
                                          &alarm.AlarmCond.Entity);
		/* Domain */			  
                alarm.AlarmCond.DomainId =
                        row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX];					  

                /* Resource ID */
		//Must be SAHPI_UNSPECIFIED_RESOURCE_ID, since we can only add USER alarms.			  		
		alarm.AlarmCond.ResourceId = SAHPI_UNSPECIFIED_RESOURCE_ID;												

                /* SensorNum */
		//Undefined since user cannot add Sensor Alarms. 
                alarm.AlarmCond.SensorNum = 0;
		
		/* EventState */
		//Undefined since user cannot add Sensor Alarms.
		alarm.AlarmCond.EventState = 0;

                /* Name */
                alarm.AlarmCond.Name.Length = 
                        row_ctx->saHpiDomainAlarmCondNameValue_len;
                memcpy(alarm.AlarmCond.Name.Value,
                       row_ctx->saHpiDomainAlarmCondNameValue,
                       row_ctx->saHpiDomainAlarmCondNameValue_len);

                /* Mid */
		//Undefined since user cannot add OEM Alarms.
                alarm.AlarmCond.Mid = 0;
		       		       										  					  							
                /* SaHpiTextBufferT Stuff */
		alarm.AlarmCond.Data.DataType = 
                        row_ctx->saHpiDomainAlarmCondTextType;

                alarm.AlarmCond.Data.Language =
                        row_ctx->saHpiDomainAlarmCondTextLanguage;
		
		alarm.AlarmCond.Data.DataLength =
                        row_ctx->saHpiDomainAlarmCondText_len;
			               
		memcpy(alarm.AlarmCond.Data.Data, 
                       row_ctx->saHpiDomainAlarmCondText, 
                       row_ctx->saHpiDomainAlarmCondText_len);


                session_id = get_session_id(row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX]);

                rc = saHpiAlarmAdd(session_id, &alarm);

                if (rc != SA_OK) {
                        DEBUGMSGTL ((AGENT, 
                                     "ERROR: saHpiAlarmAdd() "
                                     "Failed to add an Alarm rc: [%s]\n", 
                                     oh_lookup_error(rc)));
                        return get_snmp_error(rc);
                }

                keys.key_array[0] = 
                        row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX];
                keys.key_array[1] = 
                        row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX];

                /* domain_alarm_entry_get() generates unique keys based on */
                /* domainid, and entryid info.                             */
                de_entry = domain_alarm_entry_get(&keys, &doma_de_table); 

                if (de_entry == NULL) {
                        DEBUGMSGTL ((AGENT, 
                                     "ERROR: domain_alarm_add() "
                                     "domain_alarm_entry_get returned NULL\n"));
                        return AGENT_ERR_INTERNAL_ERROR;
                }

                /* we have retrieved the HPI Annunciator EntryId */
                de_entry->hpi_alarm_id = alarm.AlarmId;

        } else {
                return SNMP_ERR_NOCREATION;

        }
        
        return SNMP_ERR_NOERROR; 
}

/**
 * 
 * @param row_ctx
 * 
 * @return 
 */
int domain_alarm_ack (saHpiDomainAlarmTable_context *row_ctx)
{
        DEBUGMSGTL ((AGENT, "domain_alarm_ack, called\n"));

        SaErrorT            rc = SA_OK;
        SaHpiSessionIdT     session_id;
        SaHpiAlarmIdT       alarmId;

        DE_XREF *de_entry;
        doma_keys keys;

        if (!row_ctx)
                return AGENT_ERR_NULL_DATA;

        session_id = get_session_id(row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX]);

       /* see if this is an attempt to ack based on severtiy */
        if (row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX] == 0) {
                /* we are acking based on Severity */
                alarmId = SAHPI_ENTRY_UNSPECIFIED;
        } else {

       		keys.key_array[0] = row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX];
        	keys.key_array[1] = row_ctx->index.oids[saHpiDomainAlarmEntryId_INDEX];

                /* domain_alarm_entry_get() generates unique keys based on */
                /* domainid and alarmId tuples.                            */
                de_entry = domain_alarm_entry_get(&keys, &doma_de_table); 

                if (de_entry == NULL) {
                        DEBUGMSGTL ((AGENT, 
                                     "ERROR: domain_alarm_ack() "
                                     "domain_alarm_entry_get returned NULL\n"));
                        return AGENT_ERR_INTERNAL_ERROR;
                }

                /* we have retrieved the HPI Annunciator EntryId */
                alarmId = de_entry->hpi_alarm_id;
        }

        rc = saHpiAlarmAcknowledge(session_id,                             
                                   alarmId, 
                                   row_ctx->saHpiDomainAlarmSeverity);

        if (rc != SA_OK) {
                snmp_log (LOG_ERR,
                "domain_alarm_ack: Call to "
                "saHpiAlarmAcknowledge() failed rc: %s.\n",
                oh_lookup_error(rc));
                DEBUGMSGTL ((AGENT,
                "domain_alarm_ack: Call to "
                "saHpiAlarmAcknowledge() failed rc: %s.\n",
                oh_lookup_error(rc)));
                return get_snmp_error(rc);
        } 

        DEBUGMSGTL ((AGENT, "domain_alarm_ack: Call to "
                   "saHpiAlarmAcknowledge() succeeded rc: %s.\n",
                   oh_lookup_error(rc)));

        /* if we ack'd based on severity then we need to set ack to true   */
        /* for all rows for the given DomainId and Severity               */
        if (alarmId == SAHPI_ENTRY_UNSPECIFIED) {

                netsnmp_index *row_idx;
                saHpiDomainAlarmTable_context *doma_ctx;

                row_idx = CONTAINER_FIRST(cb.container);
                if (row_idx) //At least one entry was found.
                {
                        do {
                                doma_ctx = CONTAINER_FIND(cb.container, row_idx);

                                if ((doma_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX] ==
                                     row_ctx->index.oids[saHpiDomainAlarmDomainId_INDEX]) &&

                                    ((doma_ctx->saHpiDomainAlarmSeverity == 
                                      row_ctx->saHpiDomainAlarmSeverity) ||
                                    (row_ctx->saHpiDomainAlarmSeverity == 
                                     SAHPI_ALL_SEVERITIES)))

                                     row_ctx->saHpiDomainAlarmAcknowledged = MIB_TRUE;
                                     DEBUGMSGTL ((AGENT, "domain_alarm_ack: found row for "
                                                         "setting ack based on severity\n"));

                                row_idx = CONTAINER_NEXT(cb.container, row_idx);
                        } while (row_idx);
                }
        } /* end check for all rows deleted based on Severity */

        return SNMP_ERR_NOERROR; 
}


/**
 * 
 * @handler:
 * @reginfo:
 * @reqinfo:
 * @requests:
 * 
 * @return:
 */
int 
handle_saHpiDomainAlarmEntryCount(netsnmp_mib_handler *handler,
                                netsnmp_handler_registration *reginfo,
                                netsnmp_agent_request_info   *reqinfo,
                                netsnmp_request_info         *requests)
{
        /* We are never called for a GETNEXT if it's registered as a
           "instance", as it's "magically" handled for us.  */
        /* a instance handler also only hands us one request at a time, so
           we don't need to loop over a list of requests; we'll only get one. */

        DEBUGMSGTL ((AGENT, "handle_saHpiDomainAlarmEntryCount, called\n"));

        domain_alarm_entry_count = CONTAINER_SIZE (cb.container);
	       
        switch(reqinfo->mode) {

        case MODE_GET:
                snmp_set_var_typed_value(requests->requestvb, ASN_COUNTER,
        			        (u_char *) &domain_alarm_entry_count,
        			        sizeof(domain_alarm_entry_count));
                break;


        default:
                /* we should never get here, so this is a really bad error */
                return SNMP_ERR_GENERR;
        }

        return SNMP_ERR_NOERROR;
}

 
