/************************************************************
 * the *_create_row routine is called by the table handler
 * to create a new row for a given index. If you need more
 * information (such as column values) to make a decision
 * on creating rows, you must create an initial row here
 * (to hold the column values), and you can examine the
 * situation in more detail in the *_set_reserve1 or later
 * states of set processing. Simple check for a NULL undo_ctx
 * in those states and do detailed creation checking there.
 *
 * returns a newly allocated saHpiSensorThdUpMinorTable_context
 *   structure if the specified indexes are not illegal
 * returns NULL for errors or illegal index values.
 */
saHpiSensorThdUpMinorTable_context *
saHpiSensorThdUpMinorTable_create_row (netsnmp_index * hdr)
{
  saHpiSensorThdUpMinorTable_context *ctx =
    SNMP_MALLOC_TYPEDEF (saHpiSensorThdUpMinorTable_context);
  if (!ctx)
    return NULL;

  if (saHpiSensorThdUpMinorTable_extract_index (ctx, hdr))
    {
      free (ctx->index.oids);
      free (ctx);
      return NULL;
    }
  ctx->saHpiSensorThdUpMinorIsReadable = MIB_FALSE;
  ctx->saHpiSensorThdUpMinorIsWritable = MIB_FALSE;
  ctx->saHpiSensorThdUpMinorIsFixed = MIB_FALSE;
  ctx->saHpiSensorThdUpMinorValuesPresent = 0;
  return ctx;
}


/************************************************************
 * the *_duplicate row routine
 */
saHpiSensorThdUpMinorTable_context
  * saHpiSensorThdUpMinorTable_duplicate_row
  (saHpiSensorThdUpMinorTable_context * row_ctx)
{
  saHpiSensorThdUpMinorTable_context *dup;

  if (!row_ctx)
    return NULL;

  dup = SNMP_MALLOC_TYPEDEF (saHpiSensorThdUpMinorTable_context);
  if (!dup)
    return NULL;

  if (saHpiSensorThdUpMinorTable_row_copy (dup, row_ctx))
    {
      free (dup);
      dup = NULL;
    }

  return dup;
}

