/************************************************************
 * the *_row_copy routine
 */

static int
saHpiCtrlTable_row_copy(saHpiCtrlTable_context * dst,
                        saHpiCtrlTable_context * src)
{
    if (!dst || !src)
        return 1;

  
    if (dst->index.oids)
        free(dst->index.oids);
    if (snmp_clone_mem((void *) &dst->index.oids, src->index.oids,
                       src->index.len * sizeof(oid))) {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;


  
    dst->saHpiCtrlNum = src->saHpiCtrlNum;

    dst->saHpiCtrlOutputType = src->saHpiCtrlOutputType;

    dst->saHpiCtrlBool = src->saHpiCtrlBool;

    dst->saHpiCtrlType = src->saHpiCtrlType;

    memcpy(dst->saHpiCtrlState, src->saHpiCtrlState,
           src->saHpiCtrlState_len);
    dst->saHpiCtrlState_len = src->saHpiCtrlState_len;

    dst->saHpiCtrlOem = src->saHpiCtrlOem;

    memcpy(src->saHpiCtrlRDR, dst->saHpiCtrlRDR, src->saHpiCtrlRDR_len);
    dst->saHpiCtrlRDR_len = src->saHpiCtrlRDR_len;

    dst->hash = src->hash;
    return 0;
}



/*
 * the *_extract_index routine
 */
int
saHpiCtrlTable_extract_index(saHpiCtrlTable_context * ctx,
                             netsnmp_index * hdr)
{
    /*
     * temporary local storage for extracting oid index
     */
    netsnmp_variable_list var_saHpiCtrlNum;
    int             err;

    /*
     * copy index, if provided
     */
    if (hdr) {
        netsnmp_assert(ctx->index.oids == NULL);
        if (snmp_clone_mem((void *) &ctx->index.oids, hdr->oids,
                           hdr->len * sizeof(oid))) {
            return -1;
        }
        ctx->index.len = hdr->len;
    }

    /**
     * Create variable to hold each component of the index
     */
    memset(&var_saHpiCtrlNum, 0x00, sizeof(var_saHpiCtrlNum));
    var_saHpiCtrlNum.type = ASN_UNSIGNED;
    var_saHpiCtrlNum.next_variable = NULL;


    /*
     * parse the oid into the individual components
     */
    err = parse_oid_indexes(hdr->oids, hdr->len, &var_saHpiCtrlNum);
    if (err == SNMP_ERR_NOERROR) {
        /*
         * copy components into the context structure
         */
        ctx->saHpiCtrlNum = *var_saHpiCtrlNum.val.integer;


        /*
         * TODO: check index for valid values. For EXAMPLE:
         *
         * if ( *var_saHpiCtrlNum.val.integer != XXX ) {
         *    err = -1;
         * }
         */
    }

    /*
     * parsing may have allocated memory. free it.
     */
    snmp_reset_var_buffers(&var_saHpiCtrlNum);

    return err;
}

