static u_long inventory_count = 0;

int
populate_inventory(SaHpiInventoryRecT *inventory, 
		   SaHpiResourceIdT resource_id,
		   oid *rdr_entry_oid, size_t rdr_entry_oid_len,
		   oid *inventory_oid, size_t *inventory_oid_len) 
{
  SaHpiSessionIdT session_id;
  SaHpiInventoryDataT *data = NULL;
  SaHpiUint32T data_len;
  size_t initial_size;

  int rc = AGENT_ERR_NOERROR;
  
  oid index_oid[1];
  oid column[2];
  
  netsnmp_index	inventory_index;
  saHpiInventoryTable_context	*inventory_context; 

  DEBUGMSGTL((AGENT,"\n\t--- populate_inventory: Entry.\n"));

  if (inventory) {
    inventory_index.len = 1;
    // Look at the MIB to find out what the indexs are
    index_oid[0] = inventory->EirId;
    // Possible more indexs?

    inventory_index.oids = (oid *)&index_oid;
    // We are re-populating. Check for existing entries
    inventory_context = NULL;
    inventory_context = CONTAINER_FIND(cb.container, &inventory_index);
    // If we don't find it - we create it.
    if (!inventory_context) {
      // New entry. Add it
      inventory_context = saHpiInventoryTable_create_row(&inventory_index);
    } 
    if (!inventory_context) 
      return AGENT_ERR_INTERNAL_ERROR;
	
    // Generate our full OID
    column[0] = 1;
    column[1] = COLUMN_SAHPIINVENTORYEIRID;
	     
    build_full_oid(saHpiInventoryTable_oid, saHpiInventoryTable_oid_len,
		   column, 2,
		   &inventory_index,
		   inventory_oid, MAX_OID_LEN, inventory_oid_len);
    
    // By this stage, inventory_context surely has something in it.
    // '*_modify_context' does a checksum check to see if 
    // the record needs to be altered, and if so populates with
    // information from RDR and the OIDs passed.

    // Get Inventory Data
    rc = getSaHpiSession(&session_id);
    if (rc != AGENT_ERR_NOERROR) 
      return rc;
     
    initial_size = 2*(sizeof(SaHpiInventoryDataT) + sizeof(SaHpiInventDataRecordT) + sizeof(SaHpiTextBufferT)*9);
    
memory_fixed:

    data = malloc(initial_size);
    
    if (data == NULL) {
	return AGENT_ERR_MEMORY_FAULT;
	
    }
    DEBUGMSGTL((AGENT,"Calling InventoryRead with buffer size %d\n", initial_size));
    rc = saHpiEntityInventoryDataRead (session_id, resource_id,
					inventory->EirId,
					initial_size,
					data,
					&data_len);
    if  (rc == SA_ERR_INVENT_DATA_TRUNCATED) {
	free (data);
	data = NULL;
	initial_size *= 2;
	if (initial_size > SNMP_MAX_MSG_SIZE) {
	  // Just some random limitation.
		return AGENT_ERR_MEMORY_FAULT;

 	}
	goto memory_fixed;
    }
    DEBUGMSGTL((AGENT,"rc is %d, SA_OK is %d\n", rc, SA_OK));
    DEBUGMSGTL((AGENT,"Data validty is : %d\n", data->Validity));

    if (rc != SA_OK) {
	free(data);
	data = NULL;
	return AGENT_ERR_OPERATION;

    }
    if((data->Validity == SAHPI_INVENT_DATA_VALID) || 
       (data->Validity == SAHPI_INVENT_DATA_INVALID)) 
    {
        if (saHpiInventoryTable_modify_context(inventory, 
					       resource_id,
					       data, data_len,
					       rdr_entry_oid, rdr_entry_oid_len,
					       inventory_context)
	    == AGENT_NEW_ENTRY) {

	  CONTAINER_INSERT(cb.container, inventory_context);	  
	  inventory_count = CONTAINER_SIZE(cb.container);


	} else {
	  // INVALID data 
	  DEBUGMSGTL((AGENT,"Inventory.Validity == SAHPI_INVENT_DATA_INVALID"));
	}
    }
    // Just finish the data.
    free(data);
    data = NULL;
    DEBUGMSGTL((AGENT,"Do we ever get here?\n"));
    rc = AGENT_ERR_NOERROR;
  }
   else
    rc = AGENT_ERR_OPERATION;
  

  if (free) 
	DEBUGMSGTL((AGENT,"free != NULL, possible memory leak.\n"));
  DEBUGMSGTL((AGENT,"\n\t--- populate_inventory. Exit\n"));
  return rc;
 

}

int  
saHpiInventoryTable_modify_context(
				   SaHpiInventoryRecT *entry,SaHpiResourceIdT resource_id,
				   SaHpiInventoryDataT *inv_data, SaHpiUint32T inv_data_size,
				   oid *rdr_entry, size_t rdr_entry_oid_len,
				   saHpiInventoryTable_context *ctx) {

  long hash;
  SaHpiInventDataRecordT data;
  //SaHpiInventoryReadingT reading;
  // Make sure they are valid.
  if (entry && ctx) {
    
    // We are subtracting SaHpiTextBufferT b/c the underlaying HPI
    // library is not zeroing out the memory for not used entries -
    // thus garbage in SaHpiTextBufferT exist,
    hash = calculate_hash_value(entry, sizeof(SaHpiInventoryRecT));
    
    DEBUGMSGTL((AGENT," Hash value: %d, in ctx: %d\n", hash, ctx->hash));

    if (ctx->hash != 0) {
      // Only do the check if the hash value is something else than zero.
      // 'zero' value is only for newly created records, and in some
      // rare instances when the hash has rolled to zero - in which
      // case we will just consider the worst-case scenario and update
      // the record and not trust the hash value.
      if (hash == ctx->hash) {
	// The same data. No need to change.
	return AGENT_ENTRY_EXIST;
      }
    }

    ctx->hash = hash;

    DEBUGMSGTL((AGENT,"Creating columns for: %d\n", entry->EirId));
    ctx->saHpiInventoryRDR_len = rdr_entry_oid_len*sizeof(oid);
    memcpy(ctx->saHpiInventoryRDR, rdr_entry, ctx->saHpiInventoryRDR_len);
   
    ctx->saHpiInventoryEirId = entry->EirId;
    // How many do we have? Only one it seems?
    if (inv_data->DataRecords[0]) {
        data = *(inv_data->DataRecords[0]);
	ctx->saHpiInventoryValidity = inv_data->Validity;	
	ctx->saHpiInventoryRecordType = data.RecordType;
	ctx->saHpiInventoryAttributes_len = 0;
	memset(ctx->saHpiInventoryAttributes, 0x00, SAHPI_INVENTORY_ATTRIBUTES_MAX);
	ctx->saHpiInventoryManufacturer_len = 0;
	ctx->saHpiInventoryProductName_len = 0;
	ctx->saHpiInventoryProductVersion_len = 0;
	ctx->saHpiInventoryModelNumber_len = 0;
	ctx->saHpiInventorySerialNumber_len = 0;
	ctx->saHpiInventoryPartNumber_len = 0;
	ctx->saHpiInventoryFileId_len = 0;
	ctx->saHpiInventoryAssetTag_len = 0;
	ctx->saHpiInventoryCustomField_len = 0;
	// MIB, #007 comment. Awaiting
	ctx->saHpiInventoryTextType = 0;
	ctx->saHpiInventoryTextLanguage = 0;

	switch (data.RecordType) {
	case SAHPI_INVENT_RECTYPE_INTERNAL_USE:
		ctx->saHpiInventoryAttributes_len = data.DataLength;
		memcpy(ctx->saHpiInventoryAttributes, 
			data.RecordData.InternalUse.Data,
			data.DataLength);
		break;
	case SAHPI_INVENT_RECTYPE_CHASSIS_INFO:
		ctx->saHpiInventoryAttributes_len = 1 + sizeof(SaHpiTimeT);
		ctx->saHpiInventoryAttributes[0] = data.RecordData.ChassisInfo.Type;
		memcpy(ctx->saHpiInventoryAttributes+1,
			&data.RecordData.ChassisInfo.GeneralData.MfgDateTime,
			sizeof(SaHpiTimeT));	
		update_context_on_inventory_data(&data.RecordData.ChassisInfo.GeneralData,
						  ctx);	
		break;
	case SAHPI_INVENT_RECTYPE_BOARD_INFO:
		ctx->saHpiInventoryAttributes_len = sizeof(SaHpiTimeT);
                memcpy(ctx->saHpiInventoryAttributes,
                        &data.RecordData.BoardInfo.MfgDateTime,
                        sizeof(SaHpiTimeT));
                update_context_on_inventory_data(&data.RecordData.BoardInfo,
                                                  ctx);
		break;	
	case SAHPI_INVENT_RECTYPE_PRODUCT_INFO:
		ctx->saHpiInventoryAttributes_len =  sizeof(SaHpiTimeT);
                memcpy(ctx->saHpiInventoryAttributes,
                        &data.RecordData.ProductInfo.MfgDateTime,
                        sizeof(SaHpiTimeT));
                update_context_on_inventory_data(&data.RecordData.ProductInfo,
                                                  ctx);
		break;
	case SAHPI_INVENT_RECTYPE_OEM:
		ctx->saHpiInventoryAttributes_len =  sizeof(SaHpiManufacturerIdT);
		data.RecordData.OemData.MId = htonl(data.RecordData.OemData.MId);
                memcpy(ctx->saHpiInventoryAttributes,
                        &data.RecordData.OemData.MId,
                        sizeof(SaHpiManufacturerIdT));
		// Extra data?
		memcpy(ctx->saHpiInventoryAttributes + ctx->saHpiInventoryAttributes_len,
			&data.RecordData.OemData.Data,
			data.DataLength - sizeof(SaHpiManufacturerIdT));
		break;

	}
	
    }
    return AGENT_NEW_ENTRY;
  }
  
  return AGENT_ERR_NULL_DATA;

}
