/**
 * 
 * @sessionid:
 * @rdr_entry:
 * @rpt_entry:
 * @full_oid:
 * @full_oid_len:
 * @child_oid:
 * @child_oid_len:
 * 
 * @return 
 */
SaErrorT populate_area (SaHpiSessionIdT sessionid, 
                        SaHpiRdrT *rdr_entry,
                        SaHpiRptEntryT *rpt_entry)
{

        DEBUGMSGTL ((AGENT, "populate_area, called\n"));

        SaErrorT rv = SA_OK;
        SaHpiEntryIdT area_id;
        SaHpiIdrAreaHeaderT header;

        DRI_XREF *dri_entry;
        SaHpiDomainIdResourceIdInventoryIdArrayT dri_tuple;

        oid area_oid[AREA_INDEX_NR];
        netsnmp_index area_index;
        saHpiAreaTable_context *area_context;

        SaHpiEntryIdT subagent_area_id;

        DEBUGMSGTL ((AGENT, "SAHPI_ANNUNCIATOR_RDR populate_area() called\n"));

        /* check for NULL pointers */
        if (!rdr_entry) {
                DEBUGMSGTL ((AGENT, 
                             "ERROR: populate_area() passed NULL rdr_entry pointer\n"));
                return AGENT_ERR_INTERNAL_ERROR;
        }
        if (!rpt_entry) {
                DEBUGMSGTL ((AGENT, 
                             "ERROR: populate_area() passed NULL rdr_entry pointer\n"));
                return AGENT_ERR_INTERNAL_ERROR;
        }


        area_id = SAHPI_FIRST_ENTRY;
        do {
                rv = saHpiIdrAreaHeaderGet(sessionid, 
                                           rpt_entry->ResourceId, 
                                           rdr_entry->RdrTypeUnion.InventoryRec.IdrId,
                                           SAHPI_IDR_AREATYPE_UNSPECIFIED,
                                           area_id,
                                           &area_id,
                                           &header);
                if (rv != SA_OK) {
                        DEBUGMSGTL ((AGENT, "saHpiIdrAreaHeaderGet Failed: rv = %d\n",rv));
                        rv =  AGENT_ERR_INTERNAL_ERROR;
                        break;
                }

                /* Create MIB AreaId value                              */
                /* this is not the same as the AreaId obtained from HPI */
                /* it is maintained in the subagent                     */
                dri_tuple.domainId_resourceId_idr_arry[0] = get_domain_id(sessionid);
                dri_tuple.domainId_resourceId_idr_arry[1] = rpt_entry->ResourceId;
                dri_tuple.domainId_resourceId_idr_arry[2] = rdr_entry->RdrTypeUnion.InventoryRec.IdrId;

                /* domain_resource_idr_get() generates unique keys based on */
                /* domainid, resourceid, and IdrId tuples.                  */
                dri_entry = domain_resource_idr_get(&dri_tuple, &dri_table); 

                if (dri_entry == NULL) {
                        DEBUGMSGTL ((AGENT, 
                                     "ERROR: populate_area() domain_resource_idr_get returned NULL\n"));
                        return AGENT_ERR_INTERNAL_ERROR;
                }
                subagent_area_id = dri_entry->entry_id++;

                /* BUILD oid for new row */
                /* assign the number of indices */
                area_index.len = AREA_INDEX_NR;
                /** Index saHpiDomainId is external */
                area_oid[0] = get_domain_id(sessionid);
                /** Index saHpiResourceId is external */
                area_oid[1] = rpt_entry->ResourceId;
                /** Index saHpiResourceIsHistorical is external */
                area_oid[2] = MIB_FALSE;
                /** Index saHpiInventoryId */
                area_oid[3] = rdr_entry->RdrTypeUnion.InventoryRec.IdrId;
                /** Index saHpiAreaId */
                area_oid[4] = subagent_area_id;
                /* assign the indices to the index */
                area_index.oids = (oid *) & area_oid;

                /* See if Row exists. */
                area_context = NULL;
                area_context = CONTAINER_FIND(cb.container, &area_index);

                if (!area_context) {
                        // New entry. Add it
                        area_context = 
                        saHpiAreaTable_create_row(&area_index);
                }
                if (!area_context) {
                        snmp_log (LOG_ERR, "Not enough memory for a Area row!");
                        return AGENT_ERR_INTERNAL_ERROR;
                }

                /** SaHpiInstrumentId = ASN_UNSIGNED */
                area_context->saHpiAreaId = subagent_area_id;

                /** SaHpiInstrumentId = ASN_UNSIGNED */
                area_context->saHpiAreaIdIndex = header.AreaId;

                /** INTEGER = ASN_INTEGER */
                area_context->saHpiAreaType = header.Type + 1;

                /** TruthValue = ASN_INTEGER */
                area_context->saHpiAreaIsReadOnly =
                (header.ReadOnly == SAHPI_TRUE) ? 
                MIB_TRUE : MIB_FALSE;

                /** RowStatus = ASN_INTEGER */
                area_context->saHpiAreaRowStatus = SNMP_ROW_ACTIVE;

                /** UNSIGNED32 = ASN_UNSIGNED */
                area_context->saHpiAreaNumDataFields = header.NumFields;

                populate_field (sessionid, rdr_entry, rpt_entry, area_context);

                CONTAINER_INSERT (cb.container, area_context);

        } while (area_id !=  SAHPI_LAST_ENTRY );

        area_entry_count = CONTAINER_SIZE (cb.container);

        return rv;
}

/**
 * 
 * @domainId
 * @resourceId
 * @idrId
 * 
 * @return 
 */
SaErrorT clear_area(SaHpiDomainIdT   domainId, 
                    SaHpiResourceIdT resourceId,
                    SaHpiIdrIdT      idrId)

{
        SaErrorT rv = SA_OK;
        netsnmp_index *row_idx;
        saHpiAreaTable_context *ctx;

        DRI_XREF *dri_entry;
        SaHpiDomainIdResourceIdInventoryIdArrayT dri_tuple;

	DEBUGMSGTL ((AGENT, "area_inventory, called\n"));	
	DEBUGMSGTL ((AGENT, "           domainId   [%d]\n", domainId));	
	DEBUGMSGTL ((AGENT, "           resourceId [%d]\n", resourceId));
	DEBUGMSGTL ((AGENT, "           idrId      [%d]\n", idrId));

        /***************************/
        /* build the key to lookup */
        /***************************/
        dri_tuple.domainId_resourceId_idr_arry[0] = domainId;
        dri_tuple.domainId_resourceId_idr_arry[1] = resourceId;
        dri_tuple.domainId_resourceId_idr_arry[2] = idrId;

        /* domain_resource_idr_get() generates unique keys based on */
        /* domainid, resourceid, and IdrId tuples.                  */
        dri_entry = domain_resoruce_idr_lookup(&dri_tuple, &dri_table);

        if (dri_entry == NULL) {
                DEBUGMSGTL ((AGENT, 
                             "INFO: area_inventory() domain_resource_idr_get"
                             " returned NULL\n"));
                rv = SA_ERR_HPI_NOT_PRESENT;
        } else {
                dri_entry->entry_id = 0;
        }

        row_idx = CONTAINER_FIRST(cb.container);
        if (row_idx) //At least one entry was found.
        {
                do {
                        /* based on the found row_idx get the pointer   */
                        /* to its context (row data)                    */
                        ctx = CONTAINER_FIND(cb.container, row_idx);

                        /* before we delete the context we should get the  */
                        /* next row (context) if any before we delete this */ 
                        /* one.                                            */
                        row_idx = CONTAINER_NEXT(cb.container, row_idx);

                        if ((ctx->index.oids[saHpiDomainId_INDEX] ==
                             domainId) &&

                            (ctx->index.oids[saHpiResourceEntryId_INDEX] ==
                             resourceId)) {                     

                                /********************************************/
                                /* Clear all the under lying tables as well */
                                /********************************************/
                                rv = clear_field(domainId, 
                                                 resourceId, 
                                                 idrId,
                                                 ctx->saHpiAreaId);

                                /* all conditions met remove row */
                                CONTAINER_REMOVE (cb.container, ctx);
                                saHpiAreaTable_delete_row (ctx);
                                area_entry_count = 
                                        CONTAINER_SIZE (cb.container);
                                DEBUGMSGTL ((AGENT, "area_inventory: "
                                                    "found row: removing\n"));
                        }

                } while (row_idx);
        } 

        return rv;
}

/**
 * 
 * @field_oids:
 * @num_fields:
 * 
 * @return:
 */
int update_num_data_fields ( oid *field_oids,  SaHpiUint32T num_fields)
{
        oid area_oid[AREA_INDEX_NR];
        netsnmp_index area_index;
        saHpiAreaTable_context *area_context;

        DEBUGMSGTL ((AGENT, "update_num_data_fields, called\n"));

        /* BUILD oid for new row */
        /* assign the number of indices */
        area_index.len = AREA_INDEX_NR;
        /** Index saHpiDomainId is external */
        area_oid[0] = field_oids[saHpiDomainId_field_INDEX];
        /** Index saHpiResourceId is external */
        area_oid[1] = field_oids[saHpiResourceEntryId_field_INDEX];
        /** Index saHpiResourceIsHistorical is external */
        area_oid[2] = field_oids[saHpiResourceIsHistorical_field_INDEX];
        /** Index saHpiInventoryId */                        
        area_oid[3] = field_oids[saHpiInventoryId_field_INDEX];
        /** Index saHpiAreaId */
        area_oid[4] = field_oids[saHpiAreaId_field_INDEX];
        /* assign the indices to the index */
        area_index.oids = (oid *) & area_oid;

        /* See if Row exists. */
        area_context = NULL;
        area_context = CONTAINER_FIND(cb.container, &area_index); 
        if (!area_context) {
                return SA_ERR_HPI_UNKNOWN;
        }

        area_context->saHpiAreaNumDataFields = num_fields;

        return SNMP_ERR_NOERROR; 
}

