int
populate_sensor (SaHpiEntryIdT rdr_id,
		 SaHpiSensorRecT * sensor,
		 SaHpiRptEntryT * rpt_entry,
		 oid * rdr_entry_oid, size_t rdr_entry_oid_len,
		 oid * sensor_oid, size_t * sensor_oid_len)
{

  int rc = AGENT_ERR_NOERROR;

  oid index_oid[SENSOR_INDEX_NR];
  oid column[2];

  netsnmp_index sensor_index;
#ifdef BUG_873961
  int i = 0;
  netsnmp_void_array *array;
  saHpiSensorTable_context *ctx;
#endif
  saHpiSensorTable_context *sensor_context;
  SaHpiSensorThresholdsT sensor_threshold;
  SaHpiSessionIdT session_id;
  SaHpiSensorEvtEnablesT enables;
  SaHpiSensorReadingT current_reading;

  DEBUGMSGTL ((AGENT, "\n\t--- populate_sensor: Entry.\n"));

  if (sensor)
    {
      sensor_index.len = SENSOR_INDEX_NR;
      // Look at the MIB to find out what the indexs are
      index_oid[0] = rpt_entry->DomainId;
      index_oid[1] = rpt_entry->ResourceId;
      index_oid[2] = sensor->Num;

      sensor_index.oids = (oid *) & index_oid;
      // We are re-populating. Check for existing entries
      sensor_context = NULL;
      sensor_context = CONTAINER_FIND (cb.container, &sensor_index);
      // If we don't find it - we create it.
#ifdef  BUG_873961
      if (!sensor_context)
	{
	  // Bug # 873961. We use the 'rdr_id' to check to see if
	  // it is the context. To do so, we have to search for it first.
	  sensor_index.len = 1;
	  // re-using the index_oid.
	  array = CONTAINER_GET_SUBSET (cb.container, &sensor_index);
	  if (array != NULL)
	    {
	      if (array->size > 0)
		{
		  for (i = 0; i < array->size; i++)
		    {
		      ctx = array->array[i];
		      if (ctx->rdr_id == rdr_id)
			{
			  // Found the duplicate entry!
			  sensor_context = ctx;
			  index_oid[1] = ctx->resource_id;
			  index_oid[2] = ctx->saHpiSensorIndex;
			  DEBUGMSGTL ((AGENT,
				       "duplicate sensor entry %d, %d, %d [rdr: %d] found.\n",
				       rpt_entry->DomainId,
				       rpt_entry->ResourceId, sensor->Num,
				       rdr_id));
			  break;
			}
		    }
		}
	      free (array->array);
	      free (array);
	      array = NULL;
	    }
	  // restoree it to its previous glory.
	  sensor_index.len = SENSOR_INDEX_NR;
	}
#endif
      if (!sensor_context)
	{
	  // New entry. Add it
	  sensor_context = saHpiSensorTable_create_row (&sensor_index);
	}
      if (!sensor_context)
	{
	  snmp_log (LOG_ERR, "Not enough memory for a sensor row!");
	  return AGENT_ERR_INTERNAL_ERROR;
	}
      // Generate our full OID
      column[0] = 1;
      column[1] = COLUMN_SAHPISENSORINDEX;

      build_full_oid (saHpiSensorTable_oid, saHpiSensorTable_oid_len,
		      column, 2,
		      &sensor_index, sensor_oid, MAX_OID_LEN, sensor_oid_len);

      // By this stage, sensor_context surely has something in it.
      // '*_modify_context' does a checksum check to see if 
      // the record needs to be altered, and if so populates with
      // information from RDR and the OIDs passed.

      // Get Threshold Data

      rc = getSaHpiSession (&session_id);
      if (rc != AGENT_ERR_NOERROR)
	{
	  DEBUGMSGTL ((AGENT, "Call to getSaHpiSession failed with rc: %d\n",
		       rc));
	  return rc;
	}

      rc = saHpiSensorThresholdsGet (session_id,
				     rpt_entry->ResourceId,
				     sensor->Num, &sensor_threshold);

      if (rc != SA_OK)
	{
	  snmp_log (LOG_ERR,
		    "Call to saHpiSensorThresholdsGet fails with return code: %s.\n",
		    get_error_string (rc));
	  DEBUGMSGTL ((AGENT,
		       "Call to  SensorThresholdGet fails with return code: %s.\n",
		       get_error_string (rc)));
	  return AGENT_ERR_OPERATION;
	}

      rc = saHpiSensorEventEnablesGet (session_id,
				       rpt_entry->ResourceId,
				       sensor->Num, &enables);

      if (rc != SA_OK)
	{
	  snmp_log (LOG_ERR,
		    "Call to saHpiSensorEventEnablesGet fails with return code: %s.\n",
		    get_error_string (rc));
	  DEBUGMSGTL ((AGENT,
		       "Call to  saHpiSensorEventEnablesGet fails with return code: %s.\n",
		       get_error_string (rc)));
	  // We continue on working. No need to bail on that one - will just use 'undefined(0)' values.
	}

      rc = saHpiSensorReadingGet (session_id,
				  rpt_entry->ResourceId,
				  sensor->Num, &current_reading);

      if (rc != SA_OK)
	{
	  snmp_log (LOG_ERR,
		    "Call to saHpiSensorReadingGet fails with return code: %s.\n",
		    get_error_string (rc));
	  DEBUGMSGTL ((AGENT,
		       "Call to  saHpiSensorReadingGet fails with return code: %s.\n",
		       get_error_string (rc)));
	}



      if (saHpiSensorTable_modify_context (rdr_id, sensor,
					   &sensor_threshold,
					   &enables,
					   rpt_entry,
					   rdr_entry_oid, rdr_entry_oid_len,
					   sensor_context) == AGENT_NEW_ENTRY)
	{
	  CONTAINER_INSERT (cb.container, sensor_context);
	  sensor_count = CONTAINER_SIZE (cb.container);
	}
      /*
         Update our child sensor tables
       */



      rc = populate_ReadingCurrent (rpt_entry->DomainId,
				    rpt_entry->ResourceId,
				    sensor->Num,
				    sensor->Category, &current_reading);

      if (rc != AGENT_ERR_NOERROR)
	DEBUGMSGTL ((AGENT,
		     "call to populate_ReadingCurrent failed with rc: %d\n",
		     rc));


      if (sensor_context->flags & SAHPI_SRF_MIN)
	{
	  rc = populate_ReadingMin (rpt_entry->DomainId,
				    rpt_entry->ResourceId,
				    sensor->Num,
				    sensor->Category,
				    &sensor->DataFormat.Range.Min);
	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to populate_ReadingMin failed with rc: %d\n",
			 rc));
	}
      if (sensor_context->flags & SAHPI_SRF_MAX)
	{
	  rc = populate_ReadingMax (rpt_entry->DomainId,
				    rpt_entry->ResourceId,
				    sensor->Num,
				    sensor->Category,
				    &sensor->DataFormat.Range.Max);
	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to populate_ReadingMax failed with rc: %d\n",
			 rc));
	}

      if (sensor_context->flags & SAHPI_SRF_NOMINAL)
	{
	  rc = populate_ReadingNominal (rpt_entry->DomainId,
					rpt_entry->ResourceId,
					sensor->Num,
					sensor->Category,
					&sensor->DataFormat.Range.Nominal);
	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to populate_ReadingNominal failed with rc: %d\n",
			 rc));
	}
      if (sensor_context->flags & SAHPI_SRF_NORMAL_MAX)
	{
	  rc = populate_ReadingNormalMax (rpt_entry->DomainId,
					  rpt_entry->ResourceId,
					  sensor->Num,
					  sensor->Category,
					  &sensor->DataFormat.Range.
					  NormalMax);
	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to populate_ReadingNormalMax failed with rc: %d\n",
			 rc));
	}


      if (sensor_context->flags & SAHPI_SRF_NORMAL_MIN)
	{
	  rc = populate_ReadingNormalMin (rpt_entry->DomainId,
					  rpt_entry->ResourceId,
					  sensor->Num,
					  sensor->Category,
					  &sensor->DataFormat.Range.
					  NormalMin);
	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to populate_ReadingNormalMin failed with rc: %d\n",
			 rc));
	}

      rc = AGENT_ERR_NOERROR;
    }
  else
    rc = AGENT_ERR_OPERATION;

  DEBUGMSGTL ((AGENT, "\n\t--- populate_sensor. Exit\n"));
  return rc;
}


int
delete_sensor_row (SaHpiDomainIdT domain_id,
		   SaHpiResourceIdT resource_id, SaHpiSensorNumT sensor_num)
{

  saHpiSensorTable_context *ctx;
  oid index_oid[SENSOR_INDEX_NR];
  netsnmp_index sensor_index;
  int rc = AGENT_ERR_NOT_FOUND;

  DEBUGMSGTL ((AGENT, "delete_sensor_row (%d, %d, %d). Entry.\n",
	       domain_id, resource_id, sensor_num));
  // Look at the MIB to find out what the indexs are
  index_oid[0] = domain_id;
  index_oid[1] = resource_id;
  index_oid[2] = sensor_num;
  // Possible more indexs?
  sensor_index.oids = (oid *) & index_oid;
  sensor_index.len = SENSOR_INDEX_NR;

  ctx = CONTAINER_FIND (cb.container, &sensor_index);

  if (ctx)
    {
      rc = delete_ReadingCurrent_row (ctx->domain_id,
				      ctx->resource_id,
				      ctx->saHpiSensorIndex);


      if (rc != AGENT_ERR_NOERROR)
	DEBUGMSGTL ((AGENT,
		     "call to delete_ReadingCurrent failed with rc: %d\n",
		     rc));

      if (ctx->flags & SAHPI_SRF_MIN)
	{
	  rc = delete_ReadingMin_row (ctx->domain_id,
				      ctx->resource_id,
				      ctx->saHpiSensorIndex);

	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to delete_ReadingMin failed with rc: %d\n",
			 rc));
	}
      if (ctx->flags & SAHPI_SRF_MAX)
	{
	  rc = delete_ReadingMax_row (ctx->domain_id,
				      ctx->resource_id,
				      ctx->saHpiSensorIndex);

	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to delete_ReadingMaxfailed with rc: %d\n",
			 rc));
	}
      if (ctx->flags & SAHPI_SRF_NORMAL_MIN)
	{
	  rc = delete_ReadingNormalMin_row (ctx->domain_id,
					    ctx->resource_id,
					    ctx->saHpiSensorIndex);

	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to delete_ReadingNormalMin failed with rc: %d\n",
			 rc));
	}
      if (ctx->flags & SAHPI_SRF_NORMAL_MAX)
	{
	  rc = delete_ReadingNormalMax_row (ctx->domain_id,
					    ctx->resource_id,
					    ctx->saHpiSensorIndex);

	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to delete_ReadingNormalMax failed with rc: %d\n",
			 rc));
	}
      if (ctx->flags & SAHPI_SRF_NOMINAL)
	{
	  rc = delete_ReadingNominal_row (ctx->domain_id,
					  ctx->resource_id,
					  ctx->saHpiSensorIndex);

	  if (rc != AGENT_ERR_NOERROR)
	    DEBUGMSGTL ((AGENT,
			 "call to delete_ReadingNominal failed with rc: %d\n",
			 rc));
	}

      CONTAINER_REMOVE (cb.container, ctx);
      saHpiSensorTable_delete_row (ctx);
      sensor_count = CONTAINER_SIZE (cb.container);
      rc = AGENT_ERR_NOERROR;
    }
  DEBUGMSGTL ((AGENT, "delete_sensor_row. Exit (rc: %d).\n", rc));
  return rc;
}

int
saHpiSensorTable_modify_context (SaHpiEntryIdT rdr_id,
				 SaHpiSensorRecT * entry,
				 SaHpiSensorThresholdsT * sensor_threshold,
				 SaHpiSensorEvtEnablesT * enables,
				 SaHpiRptEntryT * rpt_entry,
				 oid * rdr_entry, size_t rdr_entry_oid_len,
				 saHpiSensorTable_context * ctx)
{
  unsigned int update_entry = MIB_FALSE;
  long hash;
  //int i;
  SaHpiSensorDataFormatT data;


  // Make sure they are valid.
  if (entry && ctx)
    {


      hash = calculate_hash_value (entry, sizeof (SaHpiSensorRecT));

      DEBUGMSGTL ((AGENT, " Hash value: %d, in ctx: %d\n", hash, ctx->hash));

      if (ctx->hash != 0)
	{
	  // Only do the check if the hash value is something else than zero.
	  // 'zero' value is only for newly created records, and in some
	  // rare instances when the hash has rolled to zero - in which
	  // case we will just consider the worst-case scenario and update
	  // the record and not trust the hash value.
	  if (hash == ctx->hash)
	    {
	      // The same data. No need to change.
	      return AGENT_ENTRY_EXIST;
	    }
	  if (((ctx->domain_id == rpt_entry->DomainId) &&
	       (ctx->resource_id == rpt_entry->ResourceId) &&
	       (ctx->saHpiSensorIndex == entry->Num)) ||
	      (ctx->rdr_id == rdr_id))
	    {
	      update_entry = MIB_TRUE;
	      DEBUGMSGTL ((AGENT, "Updating sensor entry. [%d, %d, %d]\n",
			   rpt_entry->DomainId,
			   rpt_entry->ResourceId, entry->Num));
	    }
	}
      if (hash == 0)		// Might happend - roll-over
	hash = 1;		// Need this - we consider hash values of '0' uninitialized
      ctx->hash = hash;

      data = entry->DataFormat;
      ctx->flags = data.Range.Flags;

      ctx->saHpiSensorRDR_len = rdr_entry_oid_len * sizeof (oid);
      memcpy (ctx->saHpiSensorRDR, rdr_entry, ctx->saHpiSensorRDR_len);

      ctx->saHpiSensorIndex = entry->Num;
      ctx->saHpiSensorType = entry->Type + 1;
      ctx->saHpiSensorCategory = entry->Category + 1;

      // DOAMIN
      ctx->domain_id = rpt_entry->DomainId;
      ctx->resource_id = rpt_entry->ResourceId;
      ctx->rdr_id = rdr_id;
      // IBM-KR: Adding +1
      ctx->saHpiSensorEventsCategoryControl = entry->EventCtrl + 1;

      /*
       * Generate a string representation of the state
       */

      build_state_string (entry->Category,
			  entry->Events,
			  (char *) &ctx->saHpiSensorEventsSupported,
			  &ctx->saHpiSensorEventsSupported_len,
			  SENSOR_EVENTS_SUPPORTED_MAX);

      ctx->saHpiSensorStatus = enables->SensorStatus;

      build_state_string (entry->Category,
			  enables->AssertEvents,
			  (char *) &ctx->saHpiSensorAssertEvents,
			  &ctx->saHpiSensorAssertEvents_len,
			  SENSOR_EVENTS_SUPPORTED_MAX);

      build_state_string (entry->Category,
			  enables->DeassertEvents,
			  (char *) &ctx->saHpiSensorDeassertEvents,
			  &ctx->saHpiSensorDeassertEvents_len,
			  SENSOR_EVENTS_SUPPORTED_MAX);



      ctx->saHpiSensorIgnore =
	(entry->Ignore == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;

      ctx->saHpiSensorReadingFormats = data.ReadingFormats;
      ctx->saHpiSensorIsNumeric =
	(data.IsNumeric == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;

      ctx->saHpiSensorSignFormat = data.SignFormat + 1;
      ctx->saHpiSensorBaseUnits = data.BaseUnits + 1;
      ctx->saHpiSensorModifierUnits = data.ModifierUnits + 1;
      ctx->saHpiSensorModifierUse = data.ModifierUse + 1;
      ctx->saHpiSensorFactorsStatic =
	(data.FactorsStatic == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;

      // Sensor factors.
      memset (ctx->saHpiSensorFactors, 0x00, SAHPISENSORFACTORS_MAX);
      // Normalize data.
      data.Factors.M_Factor = htons (data.Factors.M_Factor);
      data.Factors.B_Factor = htons (data.Factors.B_Factor);
      data.Factors.AccuracyFactor = htons (data.Factors.AccuracyFactor);

      memcpy (ctx->saHpiSensorFactors, &data.Factors.M_Factor,
	      sizeof (SaHpiInt16T));
      memcpy (ctx->saHpiSensorFactors + sizeof (SaHpiInt16T),
	      &data.Factors.B_Factor, sizeof (SaHpiInt16T));

      memcpy (ctx->saHpiSensorFactors + (sizeof (SaHpiInt16T) * 2),
	      &data.Factors.AccuracyFactor, sizeof (SaHpiUint16T));

      ctx->saHpiSensorFactors[6] = data.Factors.ToleranceFactor;
      ctx->saHpiSensorFactors[7] = data.Factors.ExpA;
      ctx->saHpiSensorFactors[8] = data.Factors.ExpR;
      ctx->saHpiSensorFactors[9] = data.Factors.ExpB;
      ctx->saHpiSensorFactors_len = SAHPISENSORFACTORS_MAX;

      ctx->saHpiSensorFactorsLinearization = data.Factors.Linearization + 1;

      ctx->saHpiSensorPercentage =
	(data.Percentage == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;

      ctx->saHpiSensorOEM = entry->Oem;
      if (update_entry == MIB_TRUE)
	return AGENT_ENTRY_EXIST;
      return AGENT_NEW_ENTRY;
    }

  return AGENT_ERR_NULL_DATA;
}

