    case COLUMN_SAHPISENSORPERCENTAGE:
	    /** TruthValue = ASN_INTEGER */
      snmp_set_var_typed_value (var, ASN_INTEGER,
				(char *) &context->saHpiSensorPercentage,
				sizeof (context->saHpiSensorPercentage));
