        DEBUGMSGTL ((AGENT, "populate_saHpiUserEventLogTable, called\n"));

	/* check for NULL pointers */
	if (!event) {
		DEBUGMSGTL ((AGENT, 
		"ERROR: populate_saHpiUserEventLogTable() passed NULL event pointer\n"));
		return AGENT_ERR_INTERNAL_ERROR;
	}    


        printf("POPULATING USER EVENT WITH RESOURCE OF %d\n", event->Event.Source);

