	user_evt_ctx = NULL;
	user_evt_ctx = CONTAINER_FIND(cb.container, &user_evt_idx);

	if (!user_evt_ctx) { 
		// New entry. Add it
		user_evt_ctx = 
			saHpiUserEventLogTable_create_row(&user_evt_idx);
	}
	else {
		isNewRow = MIB_FALSE;
	}	
	
	if (!user_evt_ctx) {
		snmp_log (LOG_ERR, "Not enough memory for a User Event Log row!");
		rv = AGENT_ERR_INTERNAL_ERROR;
	}


        /** SaHpiTime = ASN_COUNTER64 */
	assign_timestamp(&event->Timestamp, &user_evt_ctx->saHpiUserEventLogTimestamp);
		
        /** SaHpiTextType = ASN_INTEGER */
        user_evt_ctx->saHpiUserEventLogTextType = 
                event->Event.EventDataUnion.UserEvent.UserEventData.DataType + 1;
		
        /** SaHpiTextLanguage = ASN_INTEGER */
        user_evt_ctx->saHpiUserEventLogTextLanguage = 
                event->Event.EventDataUnion.UserEvent.UserEventData.Language + 1;	
		
        /** SaHpiText = ASN_OCTET_STR */
