static int saHpiUserEventLogTable_row_copy(saHpiUserEventLogTable_context * dst,
                         saHpiUserEventLogTable_context * src)
{
    if(!dst||!src)
        return 1;
        
    /*
     * copy index, if provided
     */
    if(dst->index.oids)
        free(dst->index.oids);
    if(snmp_clone_mem( (void*)&dst->index.oids, src->index.oids,
                           src->index.len * sizeof(oid) )) {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;
    

    /*
     * copy components into the context structure
     */
    /** TODO: add code for external index(s)! */
    dst->saHpiUserEventLogTimestamp = src->saHpiUserEventLogTimestamp;

    dst->saHpiUserEventLogTextType = src->saHpiUserEventLogTextType;

    dst->saHpiUserEventLogTextLanguage = src->saHpiUserEventLogTextLanguage;

    memcpy( dst->saHpiUserEventLogText, src->saHpiUserEventLogText, src->saHpiUserEventLogText_len );
    dst->saHpiUserEventLogText_len = src->saHpiUserEventLogText_len;

    dst->saHpiUserEventLogRowStatus = src->saHpiUserEventLogRowStatus;

    return 0;
}


