/**
 * 
 * @sessionid:
 * @event:
 * @this_child_oid:
 * @this_child_oid_len:
 * 
 * @return:
 */
SaErrorT populate_saHpiUserEventTable(SaHpiSessionIdT sessionid,
                                      SaHpiEventT *event,
                                      oid * this_child_oid, 
                                      size_t *this_child_oid_len)
{					
	SaErrorT rv = SA_OK;
        int new_row = MIB_FALSE;

	oid user_evt_oid[USER_EVENT_INDEX_NR];
	netsnmp_index user_evt_idx;
	saHpiUserEventTable_context *user_evt_ctx;

	oid column[2];
	int column_len = 2;

        DR_XREF *dr_entry;
	SaHpiDomainIdResourceIdArrayT dr_pair;

        DEBUGMSGTL ((AGENT, "populate_saHpiUserEventTable, called\n"));

	/* check for NULL pointers */
	if (!event) {
		DEBUGMSGTL ((AGENT, 
		"ERROR: populate_saHpiUserEventTable() passed NULL event pointer\n"));
		return AGENT_ERR_INTERNAL_ERROR;
	} 
	
	/* BUILD oid for new row */
		/* assign the number of indices */
	user_evt_idx.len = USER_EVENT_INDEX_NR;
		/** Index saHpiDomainId is external */
	user_evt_oid[0] = get_domain_id(sessionid);
	        /** Index saHpiEventSeverity is external */
	user_evt_oid[1] = event->Severity + 1;
                /** Index saHpiUserEventEntryId is internal */
	dr_pair.domainId_resourceId_arry[0] = get_domain_id(sessionid);
	dr_pair.domainId_resourceId_arry[1] = event->Source;
	dr_entry = domain_resource_pair_get(&dr_pair, &dr_table); 
	if (dr_entry == NULL) {
		DEBUGMSGTL ((AGENT, 
		"ERROR: populate_saHpiUserEventTable() domain_resource_pair_get returned NULL\n"));
		return AGENT_ERR_INTERNAL_ERROR;
	}
	user_evt_oid[2] = dr_entry->entry_id++;	
	user_evt_idx.oids = (oid *) & user_evt_oid;
	   
	/* See if Row exists. */
	user_evt_ctx = NULL;
	user_evt_ctx = CONTAINER_FIND(cb.container, &user_evt_idx);

	if (!user_evt_ctx) { 
		// New entry. Add it
		user_evt_ctx = 
			saHpiUserEventTable_create_row(&user_evt_idx);
                new_row = MIB_TRUE;
	}
	if (!user_evt_ctx) {
		snmp_log (LOG_ERR, "Not enough memory for a User Event row!");
		rv = AGENT_ERR_INTERNAL_ERROR;
	}

        /** SaHpiEntryId = ASN_UNSIGNED */
        user_evt_ctx->saHpiUserEventEntryId = user_evt_oid[2];

        /** SaHpiTime = ASN_COUNTER64 */
	assign_timestamp(&event->Timestamp,
			 &user_evt_ctx->saHpiUserEventTimestamp);

        /** SaHpiTextType = ASN_INTEGER */
        user_evt_ctx->saHpiUserEventTextType = 
	                event->EventDataUnion.UserEvent.UserEventData.DataType + 1;
        
        /** SaHpiTextLanguage = ASN_INTEGER */
        user_evt_ctx->saHpiUserEventTextLanguage = 
	                event->EventDataUnion.UserEvent.UserEventData.Language + 1;

        user_evt_ctx->saHpiUserEventText_len = 
	                event->EventDataUnion.UserEvent.UserEventData.DataLength;

        /** SaHpiText = ASN_OCTET_STR */			
	memcpy(user_evt_ctx->saHpiUserEventText, 
	        event->EventDataUnion.UserEvent.UserEventData.Data,
	                event->EventDataUnion.UserEvent.UserEventData.DataLength);

        /** RowStatus = ASN_INTEGER */
        user_evt_ctx->saHpiUserEventRowStatus = SAHPIUSEREVENTROWSTATUS_ACTIVE;

        if (new_row == MIB_TRUE) 
                CONTAINER_INSERT (cb.container, user_evt_ctx);
		
	user_event_entry_count = CONTAINER_SIZE (cb.container);

	/* create full oid on This row for parent RowPointer */
	column[0] = 1;
	column[1] = COLUMN_SAHPIUSEREVENTTIMESTAMP;
	memset(this_child_oid, 0, MAX_OID_LEN);
	build_full_oid(saHpiUserEventTable_oid, saHpiUserEventTable_oid_len,
			column, column_len,
			&user_evt_idx,
			this_child_oid, MAX_OID_LEN, this_child_oid_len);

        return SA_OK;   					
}

SaErrorT async_user_event_add(SaHpiSessionIdT sessionid,
                              SaHpiEventT *event,
		              SaHpiRdrT *rdr,
		              SaHpiRptEntryT *rpt_entry,			      
                              oid * this_child_oid, 
                              size_t *this_child_oid_len)
{					
	SaErrorT rv = SA_OK;
        int new_row = MIB_FALSE;

	oid user_evt_oid[USER_EVENT_INDEX_NR];
	netsnmp_index user_evt_idx;
	netsnmp_index *row_idx;
	saHpiUserEventTable_context *user_evt_ctx;

        oid column[2];
        int column_len = 2;
        
        DR_XREF *dr_entry;
        SaHpiDomainIdResourceIdArrayT dr_pair;

        SaHpiBoolT entry_found = SAHPI_FALSE;

        DEBUGMSGTL ((AGENT, "async_user_event_add, called\n"));

	/* check for NULL pointers */
	if (!event) {
		DEBUGMSGTL ((AGENT, 
		"ERROR: async_user_event_add() passed NULL event pointer\n"));
		return AGENT_ERR_INTERNAL_ERROR;
	}

                    
        row_idx = CONTAINER_FIRST(cb.container);
        
	if (row_idx) //At least one entry was found.
	{
	        do {

                        user_evt_ctx = CONTAINER_FIND(cb.container, row_idx);

                        //SaHpiTimeT tmp_ts;
                        //tmp_ts = (SaHpiTimeT)user_evt_ctx->saHpiUserEventTimestamp;

                        if ( !memcmp(user_evt_ctx->saHpiUserEventText, 
                             event->EventDataUnion.UserEvent.UserEventData.Data,
                             event->EventDataUnion.UserEvent.UserEventData.DataLength) &&

                           (user_evt_ctx->saHpiUserEventText_len == 
                           event->EventDataUnion.UserEvent.UserEventData.DataLength) &&

                           ( (user_evt_ctx->saHpiUserEventTextLanguage - 1) ==
                              event->EventDataUnion.UserEvent.UserEventData.Language) &&

                           ( (user_evt_ctx->saHpiUserEventTextType - 1) == 
                              event->EventDataUnion.UserEvent.UserEventData.DataType) &&

                           (compare_timestamp(&event->Timestamp, 
			                      &user_evt_ctx->saHpiUserEventTimestamp) == 0)  &&

                           ( (user_evt_ctx->index.oids[saHpiEventSeverity_event_INDEX] - 1) == 
                             event->Severity) &&

                           (user_evt_ctx->saHpiEventAdd_called == SAHPI_TRUE) && 

                           (user_evt_ctx->saHpiUserEventRowStatus == 
                                     SAHPIUSEREVENTROWSTATUS_CREATEANDWAIT) ) {

                                /* existing event found, set the row_status to active   */
                                /* now need to add event to saHpiEventTabel             */ 
                                DEBUGMSGTL ((AGENT, 
                                "Setting saHpiUserEventRowStatus to SAHPIUSEREVENTROWSTATUS_ACTIVE\n"));

                                /* the whole point of looking for it */
                                user_evt_ctx->saHpiUserEventRowStatus = 
                                              SAHPIUSEREVENTROWSTATUS_ACTIVE;

                               /* flag its found so we don't create a new row */
                               entry_found = SAHPI_TRUE;

                               /* place row_idx values into structure used by build full oid */
                               user_evt_idx.len = row_idx->len;
                               user_evt_idx.oids = row_idx->oids;

                               break;
                        }
                row_idx = CONTAINER_NEXT(cb.container, row_idx);
                
		} while (row_idx);
        }
        /* check if exsiting row was found. If not found which should not happen */
        /* For now add new row.  This may not be the best solution.              */

        if (!entry_found) {

        	/* BUILD oid for new row */
        		/* assign the number of indices */
        	user_evt_idx.len = USER_EVENT_INDEX_NR;
        		/** Index saHpiDomainId is external */
        	user_evt_oid[0] = get_domain_id(sessionid);
        	        /** Index saHpiEventSeverity is external */
        	user_evt_oid[1] = event->Severity + 1;
                        /** Index saHpiUserEventEntryId is internal */
        	dr_pair.domainId_resourceId_arry[0] = get_domain_id(sessionid);
        	dr_pair.domainId_resourceId_arry[1] = event->Source;
        	dr_entry = domain_resource_pair_get(&dr_pair, &dr_table); 
        	if (dr_entry == NULL) {
        		DEBUGMSGTL ((AGENT, 
        		"ERROR: async_user_event_add() domain_resource_pair_get returned NULL\n"));
        		return AGENT_ERR_INTERNAL_ERROR;
        	}
        	user_evt_oid[2] = dr_entry->entry_id++;	
        	user_evt_idx.oids = (oid *) & user_evt_oid;
        	   
        	/* See if Row exists. */
        	user_evt_ctx = NULL;
        	user_evt_ctx = CONTAINER_FIND(cb.container, &user_evt_idx);
        
        	if (!user_evt_ctx) { 
        		// New entry. Add it
        		user_evt_ctx = 
        			saHpiUserEventTable_create_row(&user_evt_idx);
                        new_row = MIB_TRUE;
                        if (!user_evt_ctx) {
                                snmp_log (LOG_ERR, 
                                "async_user_event_add: Not enough memory for a User Event row!");
                                rv = AGENT_ERR_INTERNAL_ERROR;
                        }
        
                       /* new row fill in everything */
                               /** SaHpiEntryId = ASN_UNSIGNED */
                        user_evt_ctx->saHpiUserEventEntryId = user_evt_oid[2];
        
                        /** SaHpiTime = ASN_COUNTER64 */
			assign_timestamp(&event->Timestamp, &user_evt_ctx->saHpiUserEventTimestamp);
        
                        /** SaHpiTextType = ASN_INTEGER */
                        user_evt_ctx->saHpiUserEventTextType = 
        	                event->EventDataUnion.UserEvent.UserEventData.DataType + 1;
                
                        /** SaHpiTextLanguage = ASN_INTEGER */
                        user_evt_ctx->saHpiUserEventTextLanguage = 
        	                event->EventDataUnion.UserEvent.UserEventData.Language + 1;
        
                        user_evt_ctx->saHpiUserEventText_len = 
        	                event->EventDataUnion.UserEvent.UserEventData.DataLength;
        
                        /** SaHpiText = ASN_OCTET_STR */			
                        memcpy(user_evt_ctx->saHpiUserEventText, 
        	        event->EventDataUnion.UserEvent.UserEventData.Data,
        	                event->EventDataUnion.UserEvent.UserEventData.DataLength);
        
                        /** RowStatus = ASN_INTEGER */
                        user_evt_ctx->saHpiUserEventRowStatus = SAHPIUSEREVENTROWSTATUS_ACTIVE;

                        if (new_row == MIB_TRUE) 
                                CONTAINER_INSERT (cb.container, user_evt_ctx);

                        user_event_entry_count = CONTAINER_SIZE (cb.container);
                }
        }

        /* create full oid on This row for parent RowPointer */
	column[0] = 1;
	column[1] = COLUMN_SAHPIUSEREVENTTIMESTAMP;
	memset(this_child_oid, 0, MAX_OID_LEN);
	build_full_oid(saHpiUserEventTable_oid, saHpiUserEventTable_oid_len,
			column, column_len,
			&user_evt_idx,
			this_child_oid, MAX_OID_LEN, this_child_oid_len);

        return SA_OK;   					
}


/**
 * 
 * @row_ctx:
 * 
 * @return: 
 */
int user_event_add (saHpiUserEventTable_context *row_ctx)
{
        SaErrorT        rc = SA_OK;
        SaHpiSessionIdT session_id;
        SaHpiEventT     event;

        DEBUGMSGTL ((AGENT, "user_event_add() called\n"));
	
	if (get_run_threaded() == TRUE) {
        	g_mutex_lock(thread_mutex);
	}	

          if ((row_ctx->text_type_set     == MIB_TRUE) &&
              (row_ctx->text_language_set == MIB_TRUE) &&
              (row_ctx->text_set          == MIB_TRUE)) {

                if (!row_ctx)
                        return AGENT_ERR_NULL_DATA;

                session_id = get_session_id(row_ctx->index.oids[saHpiDomainId_event_INDEX]);

                event.EventType = SAHPI_ET_USER;
                event.Severity  = row_ctx->index.oids[saHpiEventSeverity_event_INDEX] - 1;
                event.Source    = SAHPI_UNSPECIFIED_RESOURCE_ID;
                //event.Timestamp = 0; shall be set by hpi implementation 
                memcpy(event.EventDataUnion.UserEvent.UserEventData.Data,
                       row_ctx->saHpiUserEventText,
                       row_ctx->saHpiUserEventText_len);
                event.EventDataUnion.UserEvent.UserEventData.DataLength =
                        row_ctx->saHpiUserEventText_len;
                event.EventDataUnion.UserEvent.UserEventData.DataType =
                        row_ctx->saHpiUserEventTextType - 1;
                event.EventDataUnion.UserEvent.UserEventData.Language = 
                        row_ctx->saHpiUserEventTextLanguage -1;


                /* the premise here is we won't set the row to active until */
                /* the event is received from the event handling thread */
                /* at which point we will need to set this tables RowStatus */
                /* to ACTIVE(1) and update the EventTable as well */
                rc = saHpiEventAdd (session_id, &event);
                if (rc != SA_OK) {
                        snmp_log (LOG_ERR,
                                  "user_event_add: Call to saHpiEventAdd() failed to set Mode rc: %s.\n",
                                  oh_lookup_error(rc));
                        DEBUGMSGTL ((AGENT,
                                     "user_event_add: Call to saHpiEventAdd() failed to set Mode rc: %s.\n",
                                     oh_lookup_error(rc)));
                        return get_snmp_error(rc);
                }
		
		assign_timestamp(&event.Timestamp,
			         &row_ctx->saHpiUserEventTimestamp);			 
						 	
                row_ctx->saHpiEventAdd_called = MIB_TRUE;
        }   
	       
	if (get_run_threaded() == TRUE) {
        	g_mutex_unlock(thread_mutex);
	}	

        return SNMP_ERR_NOERROR; 
}

/**
 * 
 * @row_ctx:
 * 
 * @return: 
 */
int user_event_delete (saHpiUserEventTable_context *row_ctx)
{
        DEBUGMSGTL ((AGENT, "user_event_add() called\n"));
                    
        return SNMP_ERR_NOERROR;
}
				    					  
/**
 * 
 * @handler:
 * @reginfo:
 * @reqinfo:
 * @requests:
 * 
 * @return:
 */					  
int
handle_saHpiUserEventLifetimeEntries(netsnmp_mib_handler *handler,
                                     netsnmp_handler_registration *reginfo,
                                     netsnmp_agent_request_info   *reqinfo,
                                     netsnmp_request_info         *requests)
{
        /* We are never called for a GETNEXT if it's registered as a
           "instance", as it's "magically" handled for us.  */
        /* a instance handler also only hands us one request at a time, so
        we don't need to loop over a list of requests; we'll only get one. */

        DEBUGMSGTL ((AGENT, "handle_saHpiUserEventLifetimeEntries, called\n"));


        switch(reqinfo->mode) {

        case MODE_GET:
                snmp_set_var_typed_value(requests->requestvb, ASN_COUNTER,
                                         (u_char *) &user_event_entry_count_total,
                                         sizeof(user_event_entry_count_total));
                break;


        default:
                /* we should never get here, so this is a really bad error */
                return SNMP_ERR_GENERR;
        }

        return SNMP_ERR_NOERROR;
} 

/**
 * 
 * @handler:
 * @reginfo:
 * @reqinfo:
 * @requests:
 * 
 * @return:
 */
int
handle_saHpiUserEventActiveEntries(netsnmp_mib_handler *handler,
                          netsnmp_handler_registration *reginfo,
                          netsnmp_agent_request_info   *reqinfo,
                          netsnmp_request_info         *requests)
{
        /* We are never called for a GETNEXT if it's registered as a
           "instance", as it's "magically" handled for us.  */
        /* a instance handler also only hands us one request at a time, so
           we don't need to loop over a list of requests; we'll only get one. */

        DEBUGMSGTL ((AGENT, "handle_saHpiUserEventActiveEntries, called\n"));

        user_event_entry_count = CONTAINER_SIZE (cb.container);
        
        switch(reqinfo->mode) {

        case MODE_GET:
                snmp_set_var_typed_value(requests->requestvb, ASN_GAUGE,
        			        (u_char *) &user_event_entry_count,
        			        sizeof(user_event_entry_count));
                break;


        default:
                /* we should never get here, so this is a really bad error */
                return SNMP_ERR_GENERR;
        }

        return SNMP_ERR_NOERROR;
}

