    memcpy( dst->saHpiAnnouncementEventLogEntityPath, src->saHpiAnnouncementEventLogEntityPath, src->saHpiAnnouncementEventLogEntityPath_len );
    dst->saHpiAnnouncementEventLogEntityPath_len = src->saHpiAnnouncementEventLogEntityPath_len;

    dst->saHpiAnnouncementEventLogSensorNum = src->saHpiAnnouncementEventLogSensorNum;

    memcpy( dst->saHpiAnnouncementEventLogEventState, src->saHpiAnnouncementEventLogEventState, src->saHpiAnnouncementEventLogEventState_len );
    dst->saHpiAnnouncementEventLogEventState_len = src->saHpiAnnouncementEventLogEventState_len;

