/**
 * 
 * @domainId
 * @resourceId
 * 
 * @return 
 */
SaErrorT clear_current_sensor_state(SaHpiDomainIdT domainId, 
                                    SaHpiResourceIdT resourceId)

{
        SaErrorT rv = SA_OK;
        netsnmp_index *row_idx;
        saHpiCurrentSensorStateTable_context *ctx;

	DEBUGMSGTL ((AGENT, "clear_current_sensor_state, called\n"));	
	DEBUGMSGTL ((AGENT, "           domainId   [%d]\n", domainId));	
	DEBUGMSGTL ((AGENT, "           resourceId [%d]\n", resourceId));

        row_idx = CONTAINER_FIRST(cb.container);
        if (row_idx) //At least one entry was found.
        {
                do {
                        /* based on the found row_idx get the pointer   */
                        /* to its context (row data)                    */
                        ctx = CONTAINER_FIND(cb.container, row_idx);

                        /* before we delete the context we should get the  */
                        /* next row (context) if any before we delete this */ 
                        /* one.                                            */
                        row_idx = CONTAINER_NEXT(cb.container, row_idx);

                        if ((ctx->index.oids[saHpiDomainId_INDEX] ==
                             domainId) &&

                            (ctx->index.oids[saHpiResourceEntryId_INDEX] ==
                             resourceId)) {

                                /* all conditions met remove row */
                                CONTAINER_REMOVE (cb.container, ctx);
                                saHpiCurrentSensorStateTable_delete_row (ctx);
                                DEBUGMSGTL ((AGENT, "clear_current_sensor_state: "
                                                    "found row: removing\n"));

                        }

                } while (row_idx);
        } 

        return rv;
}


/*
 * int set_table_sensor_enable (saHpiCurrentSensorStateTable_context *row_ctx) 
 */ 
int set_table_sensor_enable (saHpiCurrentSensorStateTable_context *row_ctx) 
{
	SaErrorT            rc = SA_OK;
	SaHpiSessionIdT     session_id;
	SaHpiResourceIdT    resource_id;
	SaHpiSensorNumT     sensor_num;
	SaHpiBoolT          sensor_enabled;


	DEBUGMSGTL ((AGENT, "set_table_sensor_enable, called\n"));  

	if (!row_ctx)
		return AGENT_ERR_NULL_DATA;

	session_id = get_session_id(row_ctx->index.oids[saHpiDomainId_INDEX]);
	resource_id = row_ctx->index.oids[saHpiResourceEntryId_INDEX];
	sensor_num = row_ctx->index.oids[saHpiSensorNumCurrent_INDEX]; 
	sensor_enabled = 
	(row_ctx->saHpiCurrentSensorStateSensorEnable == MIB_TRUE ) ? 
	SAHPI_TRUE : SAHPI_FALSE;

	rc = saHpiSensorEnableSet(session_id, resource_id, 
				  sensor_num, sensor_enabled);

	if (rc != SA_OK) {
		snmp_log (LOG_ERR,
			  "saHpiSensorEnableSet failed to set Mode rc: %s.\n",
			  oh_lookup_error(rc));
		DEBUGMSGTL ((AGENT,
			     "saHpiSensorEnableSet failed to set Mode rc: %s.\n",
			     oh_lookup_error(rc)));
		return get_snmp_error(rc);
	}

	return SNMP_ERR_NOERROR;
}

