int
populate_ReadingCurrent (SaHpiDomainIdT domain_id,
			 SaHpiResourceIdT resource_id,
			 SaHpiSensorNumT sensor_id,
			 SaHpiEventCategoryT sensor_category,
			 SaHpiSensorReadingT * reading)
{


  int rc = AGENT_ERR_NOERROR;

  oid index_oid[SENSOR_READING_INDEX_NR];
  netsnmp_index sensor_reading_index;
  saHpiSensorReadingCurrentTable_context *ctx;


  DEBUGMSGT ((AGENT, "\t--- populate_ReadingCurrent, Entry,\n"));

  if (reading)
    {

      /*
         Construct index 
       */
      sensor_reading_index.len = SENSOR_READING_INDEX_NR;
      index_oid[0] = domain_id;
      index_oid[1] = resource_id;
      index_oid[2] = sensor_id;
      sensor_reading_index.oids = (oid *) & index_oid;

      /*
       * Find the row.
       */
      ctx = NULL;
      ctx = CONTAINER_FIND (cb.container, &sensor_reading_index);

      if (!ctx)
	{
	  /* 
	     New entry. Create it.
	   */
	  ctx =
	    saHpiSensorReadingCurrentTable_create_row (&sensor_reading_index);
	}
      if (!ctx)
	{
	  snmp_log (LOG_ERR,
		    "Not enough memory for a sensor ReadingCurrent row!");
	  return AGENT_ERR_INTERNAL_ERROR;
	}

      if (modify_saHpiSensorReadingCurrentTable_row (domain_id,
						     resource_id,
						     sensor_id,
						     sensor_category,
						     reading,
						     ctx) == AGENT_NEW_ENTRY)
	{
	  /*
	   * Add new entry.
	   */
	  CONTAINER_INSERT (cb.container, ctx);
	}
      rc = AGENT_ERR_NOERROR;

    }
  else
    rc = AGENT_ERR_OPERATION;
  DEBUGMSGT ((AGENT, "\t--- populate_ReadingCurrent, Exit (rc: %d),\n", rc));

  return rc;
}


int
modify_saHpiSensorReadingCurrentTable_row (SaHpiDomainIdT domain_id,
					   SaHpiResourceIdT resource_id,
					   SaHpiSensorNumT sensor_num,
					   SaHpiEventCategoryT
					   sensor_category,
					   SaHpiSensorReadingT * reading,
					   saHpiSensorReadingCurrentTable_context
					   * ctx)
{

  long hash = 0;
  unsigned int update_entry = MIB_FALSE;
  //  char format[SENSOR_READING_INTER_MAX];

  DEBUGMSGTL ((AGENT, "Modify saHpiSensorReadingCurrentTable_ctx: Entry.\n"));

  if (ctx)
    {
      hash = calculate_hash_value (reading, sizeof (SaHpiSensorReadingT));

      DEBUGMSGTL ((AGENT, " Hash value: %d, in ctx: %d\n", hash, ctx->hash));

      if (ctx->hash != 0)
	{
	  /* Only do the check if the hash value is something else than zero.
	   * 'zero' value is only for newly created records, and in some
	   * rare instances when the hash has rolled to zero - in which
	   * case we will just consider the worst-case scenario and update
	   * the record and not trust the hash value.
	   */
	  if (hash == ctx->hash)
	    {
	      /* The same data. No need to change. */
	      return AGENT_ENTRY_EXIST;
	    }
	  if ((ctx->domain_id == domain_id) &&
	      (ctx->resource_id == resource_id) &&
	      (ctx->sensor_id == sensor_num))
	    {
	      update_entry = MIB_TRUE;
	      DEBUGMSGTL ((AGENT,
			   "Updating ReadingCurrentTable row [%d, %d, %d]\n",
			   domain_id, resource_id, sensor_num));
	    }
	}

      if (hash == 0)		/* Might happend - roll-over */
	hash = 1;		/* Need this - we consider hash
				 * values of '0' uninitialized */
      ctx->hash = hash;

      ctx->resource_id = resource_id;
      ctx->domain_id = domain_id;
      ctx->sensor_id = sensor_num;
      ctx->sensor_category = sensor_category;
      build_reading_strings (reading,
			     sensor_category,
			     &ctx->saHpiSensorReadingCurrentValuesPresent,
			     &ctx->saHpiSensorReadingCurrentRaw,
			     ctx->saHpiSensorReadingCurrentInterpreted,
			     &ctx->saHpiSensorReadingCurrentInterpreted_len,
			     SENSOR_READING_INTER_MAX,
			     &ctx->saHpiSensorReadingCurrentStatus,
			     ctx->saHpiSensorReadingCurrentEventStatus,
			     &ctx->saHpiSensorReadingCurrentEventStatus_len,
			     SENSOR_READING_EVENT_MAX);

      /* END */
      DEBUGMSGTL ((AGENT, "Modify saHpiSensorReadingCurrentTable_ctx: Exit"));
      if (update_entry == MIB_TRUE)
	return AGENT_ENTRY_EXIST;
      return AGENT_NEW_ENTRY;

    }

  DEBUGMSGTL ((AGENT,
	       "Modify saHpiSensorReadingCurrentTable_ctx: Exit (NULL DATA)"));

  return AGENT_ERR_NULL_DATA;
}

/************************************************************
 * the *_row_copy routine
 */
static int
  saHpiSensorReadingCurrentTable_row_copy
  (saHpiSensorReadingCurrentTable_context * dst,
   saHpiSensorReadingCurrentTable_context * src)
{
  if (!dst || !src)
    return 1;

  /*
   * copy index, if provided
   */
  if (dst->index.oids)
    free (dst->index.oids);
  if (snmp_clone_mem ((void *) &dst->index.oids, src->index.oids,
		      src->index.len * sizeof (oid)))
    {
      dst->index.oids = NULL;
      return 1;
    }
  dst->index.len = src->index.len;


  /*
   * copy components into the context structure
   */
  dst->saHpiSensorReadingCurrentValuesPresent =
    src->saHpiSensorReadingCurrentValuesPresent;

  dst->saHpiSensorReadingCurrentRaw = src->saHpiSensorReadingCurrentRaw;

  memcpy (dst->saHpiSensorReadingCurrentInterpreted,
	  src->saHpiSensorReadingCurrentInterpreted,
	  src->saHpiSensorReadingCurrentInterpreted_len);
  dst->saHpiSensorReadingCurrentInterpreted_len =
    src->saHpiSensorReadingCurrentInterpreted_len;

  dst->saHpiSensorReadingCurrentStatus = src->saHpiSensorReadingCurrentStatus;

  memcpy (dst->saHpiSensorReadingCurrentEventStatus,
	  src->saHpiSensorReadingCurrentEventStatus,
	  src->saHpiSensorReadingCurrentEventStatus_len);
  dst->saHpiSensorReadingCurrentEventStatus_len =
    src->saHpiSensorReadingCurrentEventStatus_len;

  dst->resource_id = src->resource_id;
  dst->domain_id = src->domain_id;
  dst->sensor_id = src->sensor_id;
  dst->hash = src->hash;

  return 0;
}



/**
 * the *_extract_index routine
 *
 * This routine is called when a set request is received for an index
 * that was not found in the table container. Here, we parse the oid
 * in the the individual index components and copy those indexes to the
 * context. Then we make sure the indexes for the new row are valid.
 */
int
  saHpiSensorReadingCurrentTable_extract_index
  (saHpiSensorReadingCurrentTable_context * ctx, netsnmp_index * hdr)
{
  /*
   * temporary local storage for extracting oid index
   *
   * extract index uses varbinds (netsnmp_variable_list) to parse
   * the index OID into the individual components for each index part.
   */
  netsnmp_variable_list var_saHpiDomainID;
  netsnmp_variable_list var_saHpiResourceID;
  netsnmp_variable_list var_saHpiSensorIndex;
  int err;

  /*
   * copy index, if provided
   */
  if (hdr)
    {
      netsnmp_assert (ctx->index.oids == NULL);
      if (snmp_clone_mem ((void *) &ctx->index.oids, hdr->oids,
			  hdr->len * sizeof (oid)))
	{
	  return -1;
	}
      ctx->index.len = hdr->len;
    }

  /*
   * initialize variable that will hold each component of the index.
   * If there are multiple indexes for the table, the variable_lists
   * need to be linked together, in order.
   */
  memset (&var_saHpiDomainID, 0x00, sizeof (var_saHpiDomainID));
  var_saHpiDomainID.type = ASN_UNSIGNED;	/* type hint for parse_oid_indexes */

  var_saHpiDomainID.next_variable = &var_saHpiResourceID;

  memset (&var_saHpiResourceID, 0x00, sizeof (var_saHpiResourceID));
  var_saHpiResourceID.type = ASN_UNSIGNED;	/* type hint for parse_oid_indexes */

  var_saHpiResourceID.next_variable = &var_saHpiSensorIndex;

  memset (&var_saHpiSensorIndex, 0x00, sizeof (var_saHpiSensorIndex));
  var_saHpiSensorIndex.type = ASN_UNSIGNED;	/* type hint for parse_oid_indexes */

  var_saHpiSensorIndex.next_variable = NULL;

  /*
   * parse the oid into the individual index components
   */
  err = parse_oid_indexes (hdr->oids, hdr->len, &var_saHpiDomainID);
  if (err == SNMP_ERR_NOERROR)
    {
      /*
       * copy index components into the context structure
       */
      /*
         IBM-KR: We don't copy them b/c they are not needed yet.
       */
	      /** skipping external index saHpiDomainID */

	      /** skipping external index saHpiResourceID */

	      /** skipping external index saHpiSensorIndex */
    }

  /*
   * parsing may have allocated memory. free it.
   */
  snmp_reset_var_buffers (&var_saHpiDomainID);

  return err;
}

