/**
 * 
 * @sessionid:
 * @rdr_entry:
 * @rpt_entry:
 * @full_oid:
 * @full_oid_len:
 * @child_oid:
 * @child_oid_len:
 * 
 * @return 
 */
SaErrorT populate_watchdog(SaHpiSessionIdT sessionid, 
                           SaHpiRdrT *rdr_entry,
                           SaHpiRptEntryT *rpt_entry,
                           oid *full_oid, size_t full_oid_len,
                           oid *child_oid, size_t *child_oid_len)
{

        DEBUGMSGTL ((AGENT, "populate_watchdog, called\n"));

        SaErrorT rv = SA_OK;
        int new_row = MIB_FALSE;
        SaHpiWatchdogT watchdog;
        SaHpiTextBufferT buffer;

        oid watchdog_oid[WATCHDOG_INDEX_NR];
        netsnmp_index watchdog_idx;
        saHpiWatchdogTable_context *watchdog_ctx;

        oid column[2];
        int column_len = 2;

        DEBUGMSGTL ((AGENT, "SAHPI_WATCHDOG_RDR populate_watchdog() called\n"));

        /* check for NULL pointers */
        if (!rdr_entry) {
                DEBUGMSGTL ((AGENT, 
                             "ERROR: populate_watchdog() passed NULL rdr_entry pointer\n"));
                return AGENT_ERR_INTERNAL_ERROR;
        }
        if (!rpt_entry) {
                DEBUGMSGTL ((AGENT, 
                             "ERROR: populate_watchdog() passed NULL rdr_entry pointer\n"));
                return AGENT_ERR_INTERNAL_ERROR;
        }

        /* BUILD oid for new row */
        /* assign the number of indices */
        watchdog_idx.len = WATCHDOG_INDEX_NR;
        /** Index saHpiDomainId is external */
        watchdog_oid[0] = get_domain_id(sessionid);
        /** Index saHpiResourceId is external */
        watchdog_oid[1] = rpt_entry->ResourceId;
        /** Index saHpiResourceIsHistorical is external */
        watchdog_oid[2] = MIB_FALSE;
        /** Index saHpiSensorNum */
        watchdog_oid[3] = rdr_entry->RdrTypeUnion.WatchdogRec.WatchdogNum;
        /* assign the indices to the index */
        watchdog_idx.oids = (oid *) & watchdog_oid;

        /* create full oid on This row for parent RowPointer */
        column[0] = 1;
        column[1] = COLUMN_SAHPIWATCHDOGLOG;
        memset(child_oid, 0, MAX_OID_LEN);
        build_full_oid(saHpiWatchdogTable_oid, saHpiWatchdogTable_oid_len,
                       column, column_len,
                       &watchdog_idx,
                       child_oid, MAX_OID_LEN, child_oid_len);

        /* See if Row exists. */
        watchdog_ctx = NULL;
        watchdog_ctx = CONTAINER_FIND(cb.container, &watchdog_idx);

        if (!watchdog_ctx) {
                // New entry. Add it
                watchdog_ctx = 
                saHpiWatchdogTable_create_row(&watchdog_idx);
                new_row = MIB_TRUE;
        }
        if (!watchdog_ctx) {
                snmp_log (LOG_ERR, "Not enough memory for a Annunciator row!");
                return AGENT_ERR_INTERNAL_ERROR;
        }

        rv = saHpiWatchdogTimerGet (sessionid,
                                    rpt_entry->ResourceId,
                                    rdr_entry->RdrTypeUnion.WatchdogRec.WatchdogNum,
                                    &watchdog);
        if (rv != SA_OK) {
                return AGENT_ERR_INTERNAL_ERROR;
        }


        /** SaHpiInstrumentId = ASN_UNSIGNED */
        watchdog_ctx->saHpiWatchdogNum = 
                rdr_entry->RdrTypeUnion.WatchdogRec.WatchdogNum;

        /** TruthValue = ASN_INTEGER */
        watchdog_ctx->saHpiWatchdogLog = 
                (watchdog.Log == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;

        /** TruthValue = ASN_INTEGER */
        watchdog_ctx->saHpiWatchdogRunning = 
                (watchdog.Running == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;

        /** SaHpiWatchdogTimerUse = ASN_INTEGER */
        watchdog_ctx->saHpiWatchdogTimerUseState = 
                watchdog.TimerUse + 1;

        /** INTEGER = ASN_INTEGER */
        watchdog_ctx->saHpiWatchdogTimerAction =
                watchdog.TimerAction + 1;

        /** SaHpiWatchdogPreTimerAction = ASN_INTEGER */
        watchdog_ctx->saHpiWatchdogPretimerInterrupt =
                watchdog.PretimerInterrupt + 1;

        /** UNSIGNED32 = ASN_UNSIGNED */
        watchdog_ctx->saHpiWatchdogPreTimeoutInterval =
                watchdog.PreTimeoutInterval;

        /** OCTETSTR = ASN_OCTET_STR */
        rv = oh_decode_exp_flags(watchdog.TimerUseExpFlags, &buffer);
        watchdog_ctx->saHpiWatchdogTimerUseExpFlags_len = buffer.DataLength;
        memcpy(watchdog_ctx->saHpiWatchdogTimerUseExpFlags,
               buffer.Data,
               buffer.DataLength);

        /** UNSIGNED32 = ASN_UNSIGNED */
        watchdog_ctx->saHpiWatchdogTimerInitialCount = watchdog.InitialCount;

        /** UNSIGNED32 = ASN_UNSIGNED */
        watchdog_ctx->saHpiWatchdogTimerPresentCount = watchdog.PresentCount;

        /** TruthValue = ASN_INTEGER */
        watchdog_ctx->saHpiWatchdogTimerReset = MIB_FALSE;  

        /** UNSIGNED32 = ASN_UNSIGNED */
        watchdog_ctx->saHpiWatchdogOem = 
                rdr_entry->RdrTypeUnion.WatchdogRec.Oem;

        /** RowPointer = ASN_OBJECT_ID */
        memset(watchdog_ctx->saHpiWatchdogRDR, 
               0, 
               sizeof(watchdog_ctx->saHpiWatchdogRDR));
        watchdog_ctx->saHpiWatchdogRDR_len = 
        full_oid_len * sizeof(oid);
        memcpy(watchdog_ctx->saHpiWatchdogRDR, 
               full_oid, 
               watchdog_ctx->saHpiWatchdogRDR_len);

        if (new_row == MIB_TRUE) 
                CONTAINER_INSERT (cb.container, watchdog_ctx);

        watchdog_entry_count = CONTAINER_SIZE (cb.container);

        return rv;
}

/**
 * 
 * @domainId
 * @resourceId
 * 
 * @return 
 */
SaErrorT clear_watchdog(SaHpiDomainIdT domainId, 
                        SaHpiResourceIdT resourceId)

{
        SaErrorT rv = SA_OK;
        netsnmp_index *row_idx;
        saHpiWatchdogTable_context *ctx;

	DEBUGMSGTL ((AGENT, "clear_watchdog, called\n"));	
	DEBUGMSGTL ((AGENT, "           domainId   [%d]\n", domainId));	
	DEBUGMSGTL ((AGENT, "           resourceId [%d]\n", resourceId));

        row_idx = CONTAINER_FIRST(cb.container);
        if (row_idx) //At least one entry was found.
        {
                do {
                        /* based on the found row_idx get the pointer   */
                        /* to its context (row data)                    */
                        ctx = CONTAINER_FIND(cb.container, row_idx);

                        /* before we delete the context we should get the  */
                        /* next row (context) if any before we delete this */ 
                        /* one.                                            */
                        row_idx = CONTAINER_NEXT(cb.container, row_idx);

                        if ((ctx->index.oids[watchdog_saHpiDomainId_INDEX] ==
                             domainId) &&

                            (ctx->index.oids[watchdog_saHpiResourceEntryId_INDEX] ==
                             resourceId)) {

                                /* all conditions met remove row */
                                CONTAINER_REMOVE (cb.container, ctx);
                                saHpiWatchdogTable_delete_row (ctx);
                                watchdog_entry_count = 
                                        CONTAINER_SIZE (cb.container);
                                DEBUGMSGTL ((AGENT, "clear_watchdog: "
                                                    "found row: removing\n"));

                        }

                } while (row_idx);
        } 

        return rv;
}

static int set_table_watchdog_set (saHpiWatchdogTable_context *row_ctx) 
{
	SaErrorT            rc = SA_OK;
	SaHpiSessionIdT     session_id;
	SaHpiResourceIdT    resource_id;
        SaHpiWatchdogNumT   watchdog_num;
	SaHpiWatchdogT      watchdog; 

        SaHpiTextBufferT buffer;

	DEBUGMSGTL ((AGENT, "set_table_watchdog_set, called\n"));  

	if (!row_ctx)
		return AGENT_ERR_NULL_DATA;

        /* set the session */
        session_id = get_session_id(row_ctx->index.oids[watchdog_saHpiDomainId_INDEX]);

        /* set the session */
        resource_id = row_ctx->index.oids[watchdog_saHpiResourceEntryId_INDEX];

        /* set the WatchdogNum */
        watchdog_num = row_ctx->saHpiWatchdogNum;

        /* set the InitialCount */
        watchdog.InitialCount = row_ctx->saHpiWatchdogTimerInitialCount;

        /* set the Log */
        watchdog.Log = row_ctx->saHpiWatchdogLog;

        /* set the PresentCount */
        watchdog.PresentCount = row_ctx->saHpiWatchdogTimerPresentCount;

        /* set the PreTimeoutInterval */
        watchdog.PreTimeoutInterval = row_ctx->saHpiWatchdogPreTimeoutInterval;

        /* set the PretimerInterrupt */
        watchdog.PretimerInterrupt = row_ctx->saHpiWatchdogPretimerInterrupt - 1;

        /* set the Running */
        watchdog.Running = row_ctx->saHpiWatchdogRunning;

        /* set the TimerAction */
        watchdog.TimerAction = row_ctx->saHpiWatchdogTimerAction - 1;

        /* set the TimerUse */
        watchdog.TimerUse = row_ctx->saHpiWatchdogTimerUseState - 1;

        /* set the exp flags */
        buffer.DataLength = row_ctx->saHpiWatchdogTimerUseExpFlags_len;
        memcpy(buffer.Data, row_ctx->saHpiWatchdogTimerUseExpFlags, row_ctx->saHpiWatchdogTimerUseExpFlags_len);
        watchdog.TimerUseExpFlags = 0;

        if (oh_encode_exp_flags(&buffer, &watchdog.TimerUseExpFlags) != SNMP_ERR_NOERROR) {
                return SNMP_ERR_GENERR;
        }

	rc = saHpiWatchdogTimerSet (session_id, resource_id, 
                                    watchdog_num, &watchdog);

	if (rc != SA_OK) {
		snmp_log (LOG_ERR,
			  "set_table_watchdog_set: Call to saHpiWatchdogTimerSet failed to set Mode rc: %s.\n",
			  oh_lookup_error(rc));
		DEBUGMSGTL ((AGENT,
			     "set_table_watchdog_set: Call to saHpiWatchdogTimerSet failed to set Mode rc: %s.\n",
			     oh_lookup_error(rc)));
		return get_snmp_error(rc);
	}  else {
                DEBUGMSGTL ((AGENT, "set_table_watchdog_set: Success!!!\n"));

        }

	return SNMP_ERR_NOERROR;
}


