/************************************************************
 * the *_duplicate row routine
 */
saHpiWatchdogTable_context *
saHpiWatchdogTable_duplicate_row( saHpiWatchdogTable_context * row_ctx)
{
    saHpiWatchdogTable_context * dup;

    if(!row_ctx)
        return NULL;

    dup = SNMP_MALLOC_TYPEDEF(saHpiWatchdogTable_context);
    if(!dup)
        return NULL;
        
    if(saHpiWatchdogTable_row_copy(dup,row_ctx)) {
        free(dup);
        dup = NULL;
    }

    return dup;
}

